INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (246466, 'Ropivacaine', 'Netupitant', 'Minor', 'Coadministration with inhibitors of CYP450 3A4 may modestly increase the plasma concentrations of ropivacaine.  Although ropivacaine is primarily metabolized by CYP450 1A2, it has been shown to undergo some metabolism via CYP450 3A4.', NULL, 'CYP450 3A4 inhibitors have a significant effect on the pharmacokinetics of ropivacaine only in the presence of CYP450 1A2 inhibitors such as fluvoxamine, ciprofloxacin, or mexiletine.', NULL, 'CYP450 3A4 inhibitors have a significant effect on the pharmacokinetics of ropivacaine only in the presence of CYP450 1A2 inhibitors such as fluvoxamine, ciprofloxacin, or mexiletine. Therefore, the combination of ropivacaine and the above drugs is not recommended.', 'Metabolism', NULL, NULL, 'Minor', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/290761/', '[1] Halldin MM, Bredberg E, Angelin B, Arvidsson T, Askemark Y, Elofsson S, Widman M "Metabolism and excretion of ropivacaine in humans." Drug Metab Dispos 24 (1996):  962-8[2] Oda Y, Furuichi K, Tanaka K, Hiroi T, Imaoka S, Asada A, Fujimori M, Funae Y "Metabolism of a new local anesthetic, ropivacaine, by human hepatic cytochrome P450." Anesthesiology 82 (1995):  214-20[3] "Product Information. Naropin (ropivacaine)." Astra-Zeneca Pharmaceuticals  (2001):[4] McClure JH "Ropivacaine." Br J Anaesth 76 (1996):  300-7[5] Ekstrom G, Gunnarsson UB "Ropivacaine, a new amide-type local anesthetic agent, is metabolized by cytochromes P450 1A and 3A in human liver microsomes." Drug Metab Dispos 24 (1996):  955-61[6] Arlander E, Ekstrom G, Alm C, Carrillo JA, Bielenstein M, Bottiger Y, Bertilsson L, Gustafsson LL "Metabolism of ropivacaine in humans is mediated by CYP1A2 and to a minor extent by CYP3A4: An interaction study with fluvoxamine and ketoconazole as in vivo inhibitors." Clin Pharmacol Ther 64 (1998):  484-91[7] Jokinen MJ, Ahonen J, Neuvonen PJ, Olkkola KT "The effect of erythromycin, fluvoxamine, and their combination on the pharmacokinetics of ropivacaine." Anesth Analg 91 (2000):  1207-12', 1767363028);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (246467, 'Ruxolitinib', 'Netupitant', 'Minor', 'Coadministration with mild or moderate inhibitors of CYP450 3A4 may modestly increase the plasma concentrations of ruxolitinib, which is primarily metabolized by the isoenzyme.', NULL, 'Coadministration with mild or moderate inhibitors of CYP450 3A4 may modestly increase the plasma concentrations of ruxolitinib, which is primarily metabolized by the isoenzyme.', NULL, 'Coadministration with mild or moderate inhibitors of CYP450 3A4 may modestly increase the plasma concentrations of ruxolitinib, which is primarily metabolized by the isoenzyme.  In healthy subjects, administration of a single 10 mg dose of ruxolitinib following pretreatment with the moderate CYP450 3A4 inhibitor erythromycin (500 mg twice daily for four days) resulted in an 8% increase in ruxolitinib peak plasma concentration (Cmax) and a 27% increase in systemic exposure (AUC) compared to administration of ruxolitinib alone.  The change in the pharmacodynamic marker, pSTAT3 inhibition, was consistent with the corresponding exposure information.  No dosage adjustment is recommended when ruxolitinib is coadministered with mild or moderate CYP450 3A4 inhibitors.', 'Metabolism', NULL, NULL, 'Minor', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/290762/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] "Product Information. Jakafi (ruxolitinib)." Incyte Corporation  (2011):', 1767363028);
