INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (246476, 'Siponimod', 'Netupitant', 'Major', 'Coadministration with drugs that cause moderate inhibition of CYP450 2C9 and moderate or strong inhibition of CYP450 3A4 may increase the plasma concentrations of siponimod, which is primarily metabolized by these isoenzymes.  This interaction includes concomitant use of siponimod with a moderate CYP450 2C9/3A4 dual inhibitor or a moderate CYP450 2C9 inhibitor in combination with a separate moderate or strong CYP450 3A4 inhibitor.', NULL, 'Concomitant use of siponimod with a moderate or strong CYP450 2C9/3A4 dual inhibitor (e.', NULL, 'Concomitant use of siponimod with a moderate or strong CYP450 2C9/3A4 dual inhibitor (e.g., fluconazole, mifepristone) or a moderate CYP450 2C9 inhibitor (e.g., abiraterone, amiodarone, fenofibrate, gemfibrozil, nitisinone, oxandrolone) in combination with a moderate or strong CYP450 3A4 inhibitor (e.g., amprenavir, aprepitant, atazanavir, boceprevir, ceritinib, chloramphenicol, ciprofloxacin, clarithromycin, cobicistat, conivaptan, crizotinib, dalfopristin-quinupristin, darunavir, delavirdine, diltiazem, dronedarone, erythromycin, fedratinib, fluvoxamine, fosamprenavir, fosaprepitant, fosnetupitant, fusidic acid, idelalisib, imatinib, indinavir, isavuconazole, itraconazole, ketoconazole, letermovir, mibefradil, nefazodone, nelfinavir, netupitant, posaconazole, ribociclib, ritonavir, saquinavir, stiripentol, telaprevir, telithromycin, troleandomycin, verapamil, voriconazole, voxelotor) is not recommended.  Caution is advised if siponimod is used in combination with moderate CYP450 2C9 inhibitors.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/290771/', '[1] Cerner Multum, Inc. "Australian Product Information." O 0[2] "Product Information. Mayzent (siponimod)." Novartis Pharmaceuticals  (2019):', 1767363028);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (246477, 'Sirolimus', 'Netupitant', 'Moderate', 'Coadministration with drugs that are inhibitors of CYP450 3A4 may increase the blood concentrations of the macrolide immunosuppressants sirolimus and tacrolimus, both of which are metabolized by the isoenzyme.', NULL, 'The possibility of prolonged and/or increased pharmacologic effects of macrolide immunosuppressant therapy should be considered, including adverse effects such as fever, infection, diarrhea, hypokalemia, anemia, thrombocytopenia, leukopenia, and hyperlipidemia.', NULL, 'The possibility of prolonged and/or increased pharmacologic effects of macrolide immunosuppressant therapy should be considered, including adverse effects such as fever, infection, diarrhea, hypokalemia, anemia, thrombocytopenia, leukopenia, and hyperlipidemia.  Dosage adjustments as well as clinical and laboratory monitoring may be appropriate whenever a CYP450 3A4 inhibitor is added to or withdrawn from therapy.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/290772/', '[1] "Product Information. Prograf (tacrolimus)." Fujisawa  (2001):[2] "Product Information. Parlodel (bromocriptine)." Sandoz Pharmaceuticals Corporation  (2001):[3] Christians U, Schmidt G, Bader A, et al. "Identification of drugs inhibiting the in vitro metabolism of tacrolimus by human liver microsomes." Br J Clin Pharmacol 41 (1996):  187-90[4] "Product Information. Rapamune (sirolimus)." Wyeth-Ayerst Laboratories  (2001):[5] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[6] Cerner Multum, Inc. "Australian Product Information." O 0[7] "Product Information. Qelbree (viloxazine)." Supernus Pharmaceuticals Inc  (2021):', 1767363028);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (246478, 'Solifenacin', 'Netupitant', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of solifenacin, which is primarily metabolized by the isoenzyme.', NULL, 'Pharmacologic response to solifenacin should be monitored more closely whenever a CYP450 3A4 inhibitor is added to or withdrawn from therapy, and the solifenacin dosage adjusted if necessary.', NULL, 'Pharmacologic response to solifenacin should be monitored more closely whenever a CYP450 3A4 inhibitor is added to or withdrawn from therapy, and the solifenacin dosage adjusted if necessary.  Patients should be advised to contact their physician if they experience undue adverse effects of solifenacin such as severe abdominal pain or constipation for 3 or more days.  Patients should seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitations, irregular heartbeat, shortness of breath, or syncope.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/290775/', '[1] "Product Information. VESIcare (solifenacin)." GlaxoSmithKline  (2004):', 1767363028);
