INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (246482, 'Sonidegib', 'Netupitant', 'Major', 'Coadministration with moderate inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of sonidegib, which is primarily metabolized by the isoenzyme.', NULL, 'Concomitant use of sonidegib with moderate CYP450 3A4 inhibitors should generally be avoided.', NULL, 'Concomitant use of sonidegib with moderate CYP450 3A4 inhibitors should generally be avoided.  If coadministration is required, the manufacturer recommends limiting use of the moderate CYP450 3A4 inhibitor to less than 14 days and monitoring for adverse reactions, particularly musculoskeletal toxicity.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/290779/', '[1] "Product Information. Odomzo (sonidegib)." Novartis Pharmaceuticals  (2015):', 1767363028);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (246483, 'St. John''s Wort', 'Palonosetron', 'Major', 'Concomitant use of 5-HT3 receptor antagonists with agents that possess or enhance serotonergic activity such as selective serotonin reuptake inhibitors (SSRIs), selective serotonin-norepinephrine reuptake inhibitors (SNRIs), monoamine oxidase inhibitors (MAOIs), tricyclic antidepressants (TCAs), 5-HT1 receptor agonists (triptans), ergot alkaloids, phenylpiperidine opioids, bupropion, dextromethorphan, linezolid, lithium, St. John''s wort, tramadol, and tryptophan may potentiate the risk of serotonin syndrome, which is a rare but serious and potentially fatal condition thought to result from hyperstimulation of brainstem 5-HT1A and 2A receptors.', NULL, 'Caution is advised if 5-HT3 receptor antagonists are prescribed with other agents that affect the serotonergic neurotransmitter system.', NULL, 'Caution is advised if 5-HT3 receptor antagonists are prescribed with other agents that affect the serotonergic neurotransmitter system.  Patients should be closely monitored for symptoms of the serotonin syndrome during treatment.  Particular caution is warranted when initiating or increasing the dosages of these agents.  The potential risk for serotonin syndrome should be considered even when administering serotonergic agents sequentially, as some agents may demonstrate a prolonged elimination half-life.  For example, a 5-week washout period is typically recommended following use of fluoxetine and 2 weeks following use of MAOIs before administering another serotonergic agent.  If serotonin syndrome develops or is suspected during the course of therapy, all serotonergic agents should be discontinued immediately and supportive care rendered as necessary.  Moderately ill patients may also benefit from the administration of a serotonin antagonist (e.g., cyproheptadine, chlorpromazine).  Severe cases should be managed under consultation with a toxicologist and may require sedation, neuromuscular paralysis, intubation, and mechanical ventilation in addition to the other measures.', 'Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/290781/', '[1] "Product Information. Anzemet (dolasetron)." Hoechst Marion Roussel  (2001):[2] "Product Information. Aloxi (palonosetron)." MGI Pharma Inc  (2003):[3] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[4] Canadian Pharmacists Association "e-CPS.  http://www.pharmacists.ca/function/Subscriptions/ecps.cfm?link=eCPS_quikLink"   (2006):[5] Cerner Multum, Inc. "Australian Product Information." O 0[6] "Product Information. Akynzeo (netupitant-palonosetron)." Eisai Inc  (2014):[7] "Product Information. Sancuso (granisetron)." ProStrakan Group  (2014):', 1767363028);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (246484, 'St. John''s Wort', 'Netupitant', 'Major', 'Coadministration with potent inducers of CYP450 3A4 may significantly decrease the plasma concentrations of netupitant.', NULL, 'Concomitant use of netupitant or its prodrug, fosnetupitant, should generally be avoided during chronic administration of potent CYP450 3A4 inducers due to the potential for reduced efficacy of netupitant.', NULL, 'Concomitant use of netupitant or its prodrug, fosnetupitant, should generally be avoided during chronic administration of potent CYP450 3A4 inducers due to the potential for reduced efficacy of netupitant.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/290782/', '[1] "Product Information. Akynzeo for Injection (fosnetupitant-palonosetron)." Helsinn Therapeutics Inc, Woodbridge Township, NJ.[2] "Product Information. Akynzeo (netupitant-palonosetron)." Eisai Inc, Woodcliff Lake, NJ.[3] Cerner Multum, Inc. "Australian Product Information." O 0', 1767363028);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (246485, 'Stiripentol', 'Netupitant', 'Moderate', 'Coadministration with inhibitors of CYP450 1A2 and/or 3A4 may increase the plasma concentrations of stiripentol, which has been shown in vitro to undergo phase I metabolism via these pathways.', NULL, 'Caution is advised when stiripentol is used with inhibitors of CYP450 1A2 and/or 3A4 that are also substrates of these enzymatic pathways.', NULL, 'Caution is advised when stiripentol is used with inhibitors of CYP450 1A2 and/or 3A4 that are also substrates of these enzymatic pathways.  Patients should be monitored for potentially increased adverse effects of stiripentol such as anorexia, nausea, vomiting, weight loss, somnolence, dizziness, confusion, difficulty concentrating, ataxia, hypotonia, tremor, hyperkinesia, dysarthria, suicidal ideation/behavior, neutropenia, and thrombocytopenia.  In addition, clinical and laboratory monitoring may be appropriate for concomitant drugs whenever stiripentol is added to or withdrawn from therapy.  Dosage adjustments or alternative treatments may be required if an interaction is suspected.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/290783/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] EMEA. European Medicines Agency "EPARs. European Union Public Assessment Reports.  http://www.ema.europa.eu/ema/index.jsp?curl=pages/includes/medicines/medicines_landingpage.jsp&mid"   (2007):[3] "Product Information. Diacomit (stiripentol)." Biocodex USA  (2018):', 1767363028);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (246486, 'Sufentanil', 'Netupitant', 'Moderate', 'Coadministration with netupitant or its prodrug, fosnetupitant, may increase the plasma concentrations of drugs that are primarily metabolized by CYP450 3A4.  The mechanism is decreased clearance due to inhibition of CYP450 3A4 activity by netupitant and its desmethyl metabolite.', NULL, 'Caution is advised when netupitant or fosnetupitant is used concomitantly with drugs that undergo metabolism by CYP450 3A4, particularly those with a narrow therapeutic range.', NULL, 'Caution is advised when netupitant or fosnetupitant is used concomitantly with drugs that undergo metabolism by CYP450 3A4, particularly those with a narrow therapeutic range.  Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever netupitant or fosnetupitant is added to or withdrawn from therapy.  The inhibitory effect on CYP450 3A4 can last for 6 days after administration of a single dose of netupitant or fosnetupitant.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/290784/', '[1] Cerner Multum, Inc. "Australian Product Information." O 0[2] "Product Information. Akynzeo (netupitant-palonosetron)." Eisai Inc  (2014):[3] "Product Information. Akynzeo for Injection (fosnetupitant-palonosetron)." Helsinn Therapeutics Inc  (2018):', 1767363028);
