INSERT OR IGNORE INTO drug_interactions (
        id, ingredient1, ingredient2, severity, effect, arabic_effect,
        recommendation, arabic_recommendation, management_text, mechanism_text,
        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1471320, 'Iohexol', 'Esmolol', 'Moderate', 'Limited data suggest that patients receiving beta-blockers may have an increased risk of severe hypotensive and/or hypersensitivity reactions to parenteral iodinated contrast media. In addition, allergic/anaphylactoid reactions in these patients may be more difficult to treat.', NULL, 'Patients who have received beta-blockers should be closely monitored for adverse reactions to iodinated contrast media.', NULL, 'Patients who have received beta-blockers should be closely monitored for adverse reactions to iodinated contrast media. If anaphylaxis occurs, clinicians should be aware that beta-blockers may attenuate the response to epinephrine. Thus, larger doses of epinephrine may be necessary to overcome the bronchospasm, although such large doses can also cause excessive alpha-adrenergic stimulation resulting in hypertension, reflex bradycardia, heart block, and possible potentiation of bronchospasm. Alternative treatments recommended include vigorous supportive care (e.g., fluids) and the use of parenteral beta-agonists for bronchospasm and norepinephrine for hypotension.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317476);
INSERT OR IGNORE INTO drug_interactions (
        id, ingredient1, ingredient2, severity, effect, arabic_effect,
        recommendation, arabic_recommendation, management_text, mechanism_text,
        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1471321, 'Iohexol', 'Etacrynic acid', 'Moderate', 'Forced diuresis during administration of radiocontrast agents may increase the risk of renal impairment in patients who are at high risk for contrast-induced nephropathy.', NULL, 'Whenever possible, alternative imaging techniques should be considered in patients who are at high risk for contrast-induced nephropathy.', NULL, 'Whenever possible, alternative imaging techniques should be considered in patients who are at high risk for contrast-induced nephropathy. Otherwise, some experts recommend discontinuing diuretics 1 to 2 days before administration of contrast media, depending on the clinical feasibility of doing so. The smallest effective dose of a nonionic, hypo- or iso-osmolar contrast medium (e.g., iohexol, iodixanol, iopamidol) should be used, since the risk of nephropathy is increased with increasing contrast dose and/or osmolarity. Repeat procedures with contrast media, if necessary, should not occur until at least 72 hours after the previous contrast exposure and renal function has fully recovered. Although it is not necessary to measure the serum creatinine levels of every patient before contrast administration, measurements should generally be made in patients receiving contrast agent by intraarterial administration (which is associated with increased risk of nephropathy relative to intravenous administration) and patients with a history of kidney disease, proteinuria, kidney surgery, diabetes, hypertension, gout, or other risk factors for nephropathy. Creatinine measurements should be continued for 24 to 48 hours after administration of contrast medium. It is important that patients be adequately hydrated with either saline or sodium bicarbonate.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317476);
INSERT OR IGNORE INTO drug_interactions (
        id, ingredient1, ingredient2, severity, effect, arabic_effect,
