    ) VALUES (1471394, 'Iohexol', 'Prednisolone', 'Major', 'Coadministration of corticosteroids with iodinated contrast media by the intrathecal route may be associated with a high incidence of radiographic arachnoiditis. Meningitis and seizures may also occur.', NULL, 'Intrathecal coadministration of corticosteroids with iodinated contrast media is considered contraindicated.', NULL, 'Intrathecal coadministration of corticosteroids with iodinated contrast media is considered contraindicated.', 'Others', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', 1767317476);
