    ) VALUES (1471410, 'Iohexol', 'Iodide I-123', 'Moderate', 'INTERVAL: Iodine-containing products may affect the pharmacokinetics, pharmacodynamics, and/or diagnostic results of radioiodides. These effects may last up to 8 weeks.', NULL, 'Iodine preparations including expectorants, vitamins, topicals, and intravascular iodine-containing contrast media should be withheld for at least 2 to 4 weeks and possibly longer, and cholecystographic agents should be withheld for more than 4 weeks, prior to administration of sodium iodide I-131 or I-123.', NULL, 'Iodine preparations including expectorants, vitamins, topicals, and intravascular iodine-containing contrast media should be withheld for at least 2 to 4 weeks and possibly longer, and cholecystographic agents should be withheld for more than 4 weeks, prior to administration of sodium iodide I-131 or I-123.', 'Others', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317476);
