INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (246520, 'Valdecoxib', 'Netupitant', 'Moderate', 'Coadministration with netupitant or its prodrug, fosnetupitant, may increase the plasma concentrations of drugs that are primarily metabolized by CYP450 3A4.  The mechanism is decreased clearance due to inhibition of CYP450 3A4 activity by netupitant and its desmethyl metabolite.', NULL, 'Caution is advised when netupitant or fosnetupitant is used concomitantly with drugs that undergo metabolism by CYP450 3A4, particularly those with a narrow therapeutic range.', NULL, 'Caution is advised when netupitant or fosnetupitant is used concomitantly with drugs that undergo metabolism by CYP450 3A4, particularly those with a narrow therapeutic range.  Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever netupitant or fosnetupitant is added to or withdrawn from therapy.  The inhibitory effect on CYP450 3A4 can last for 6 days after administration of a single dose of netupitant or fosnetupitant.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/290822/', '[1] Cerner Multum, Inc. "Australian Product Information." O 0[2] "Product Information. Akynzeo (netupitant-palonosetron)." Eisai Inc  (2014):[3] "Product Information. Akynzeo for Injection (fosnetupitant-palonosetron)." Helsinn Therapeutics Inc  (2018):', 1767365210);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (246521, 'Vardenafil', 'Netupitant', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 and/or 2C9 may increase the plasma concentrations of vardenafil, which is primarily metabolized by the former and secondarily by the latter.', NULL, 'Dosage adjustments may be appropriate for vardenafil whenever a CYP450 3A4 and/or 2C9 inhibitor is added to or withdrawn from therapy based on efficacy and patient tolerance.', NULL, 'Dosage adjustments may be appropriate for vardenafil whenever a CYP450 3A4 and/or 2C9 inhibitor is added to or withdrawn from therapy based on efficacy and patient tolerance.  The manufacturer recommends that an initial vardenafil dosage of 2.5 to 5 mg be used in patients treated concomitantly with a potent CYP450 3A4 inhibitor such as erythromycin, itraconazole, ketoconazole, indinavir, and ritonavir.  No specific dosage recommendations are available for coadministration with less potent inhibitors.  Patients should be advised to promptly notify their doctor if they experience pain or tightness in the chest or jaw, irregular heartbeat, nausea, shortness of breath, visual disturbances, syncope, or prolonged erection (greater than 4 hours).', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/290823/', '[1] "Product Information. Levitra (vardenafil)." Bayer  (2003):[2] "Product Information. Qelbree (viloxazine)." Supernus Pharmaceuticals Inc  (2021):', 1767365210);
