INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (246547, 'Fosnetupitant', 'Apixaban', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 and/or P-glycoprotein (P-gp) may increase the plasma concentrations of apixaban, which is a substrate of both the isoenzyme and efflux transporter.', NULL, 'No dosage adjustment for apixaban is required during concomitant therapy with lone inhibitors of CYP450 3A4 or P-gp, or moderate or weak dual inhibitors of both CYP450 3A4 and P-gp.', NULL, 'No dosage adjustment for apixaban is required during concomitant therapy with lone inhibitors of CYP450 3A4 or P-gp, or moderate or weak dual inhibitors of both CYP450 3A4 and P-gp.  However, caution may be advisable.  Closer monitoring of the pharmacologic effects of apixaban may be appropriate whenever a CYP450 3A4 or P-gp inhibitor is added to or withdrawn from therapy.  Patients should be routinely evaluated for signs and symptoms suggesting blood loss such as a drop in hemoglobin and/or hematocrit, hypotension, or fetal distress (in pregnant women).', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/290852/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] "Product Information. Eliquis (apixaban)." Bristol-Myers Squibb Canada Inc  (2012):[3] "Product Information. Qelbree (viloxazine)." Supernus Pharmaceuticals Inc  (2021):', 1767365210);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (246548, 'Fosnetupitant', 'Aprepitant', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of aprepitant, which is primarily metabolized by the isoenzyme.', NULL, 'Caution is advised if aprepitant is prescribed in combination with CYP450 3A4 inhibitors, particularly potent ones like itraconazole, ketoconazole, posaconazole, voriconazole, clarithromycin, conivaptan, idelalisib, nefazodone, telithromycin, cobicistat, delavirdine, and protease inhibitors.', NULL, 'Caution is advised if aprepitant is prescribed in combination with CYP450 3A4 inhibitors, particularly potent ones like itraconazole, ketoconazole, posaconazole, voriconazole, clarithromycin, conivaptan, idelalisib, nefazodone, telithromycin, cobicistat, delavirdine, and protease inhibitors.  Pharmacologic response to aprepitant should be monitored more closely whenever a CYP450 3A4 inhibitor is added to or withdrawn from therapy.  In addition, many CYP450 3A4 inhibitors are also substrates of the isoenzyme, thus pharmacologic response to these agents should also be monitored during coadministration with aprepitant.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/290853/', '[1] "Product Information. Emend (aprepitant)." Merck & Co., Inc  (2003):[2] "Product Information. Emend for Injection (fosaprepitant)." Merck & Co., Inc  (2008):', 1767365210);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (246549, 'Fosnetupitant', 'Aripiprazole', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 and/or 2D6 may increase the plasma concentrations of aripiprazole, which is primarily metabolized by these isoenzymes.', NULL, 'Pharmacologic response to aripiprazole should be monitored more closely whenever a CYP450 3A4 and/or 2D6 inhibitor is added to or withdrawn from therapy, and the aripiprazole dosage adjusted as necessary.', NULL, 'Pharmacologic response to aripiprazole should be monitored more closely whenever a CYP450 3A4 and/or 2D6 inhibitor is added to or withdrawn from therapy, and the aripiprazole dosage adjusted as necessary.  The manufacturer recommends that aripiprazole dosage be reduced to one-half the normal dosage during concomitant administration with ketoconazole or quinidine, and additional dosage adjustments be made based on clinical evaluation.  No dosage recommendations are available for concomitant administration with less potent CYP450 2D6 or 3A4 inhibitors.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/290854/', '[1] "Product Information. Abilify (aripiprazole)." Bristol-Myers Squibb  (2002):', 1767365210);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (246550, 'Fosnetupitant', 'Armodafinil', 'Minor', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of modafinil (the racemate) and armodafinil (the R-enantiomer), which are both partially metabolized by the isoenzyme.  Conversely, the plasma levels of some of these inhibitors may decrease, since many of them are also substrates of CYP450 3A4, and modafinil and armodafinil have been found to be modest inducers of CYP450 3A4.', NULL, 'Clinical monitoring for altered effects of modafinil and armodafinil as well as the CYP450 3A4 inhibitor may be appropriate following addition or withdrawal of one or the other drug.', NULL, 'Clinical monitoring for altered effects of modafinil and armodafinil as well as the CYP450 3A4 inhibitor may be appropriate following addition or withdrawal of one or the other drug.  Dose adjustments may be required if an interaction is suspected.', 'Metabolism', NULL, NULL, 'Minor', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/290855/', '[1] "Product Information. Provigil (modafinil)." Cephalon, Inc  (2001):[2] "Product Information. Nuvigil (armodafinil)." Cephalon Inc  (2007):[3] Cerner Multum, Inc. "Australian Product Information." O 0', 1767365210);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (246551, 'Fosnetupitant', 'Astemizole', 'Moderate', 'Coadministration with netupitant or its prodrug, fosnetupitant, may increase the plasma concentrations of drugs that are primarily metabolized by CYP450 3A4.  The mechanism is decreased clearance due to inhibition of CYP450 3A4 activity by netupitant and its desmethyl metabolite.', NULL, 'Caution is advised when netupitant or fosnetupitant is used concomitantly with drugs that undergo metabolism by CYP450 3A4, particularly those with a narrow therapeutic range.', NULL, 'Caution is advised when netupitant or fosnetupitant is used concomitantly with drugs that undergo metabolism by CYP450 3A4, particularly those with a narrow therapeutic range.  Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever netupitant or fosnetupitant is added to or withdrawn from therapy.  The inhibitory effect on CYP450 3A4 can last for 6 days after administration of a single dose of netupitant or fosnetupitant.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/290857/', '[1] Cerner Multum, Inc. "Australian Product Information." O 0[2] "Product Information. Akynzeo (netupitant-palonosetron)." Eisai Inc  (2014):[3] "Product Information. Akynzeo for Injection (fosnetupitant-palonosetron)." Helsinn Therapeutics Inc  (2018):', 1767365210);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (246552, 'Fosnetupitant', 'Atazanavir', 'Minor', 'Coadministration with potent inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of netupitant.', NULL, 'No dosage adjustment is necessary for single-dose administration of netupitant-palonosetron when used with potent CYP450 3A4 inhibitors.', NULL, 'No dosage adjustment is necessary for single-dose administration of netupitant-palonosetron when used with potent CYP450 3A4 inhibitors.  However, patients may experience increased adverse effects such as headache, asthenia, dyspepsia, fatigue, constipation, and erythema.', 'Metabolism', NULL, NULL, 'Minor', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/290858/', '[1] "Product Information. Akynzeo (netupitant-palonosetron)." Eisai Inc  (2014):', 1767365210);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (246553, 'Fosnetupitant', 'Atorvastatin', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of HMG-CoA reductase inhibitors (i.e., statins) that are metabolized by the isoenzyme.  Lovastatin and simvastatin are particularly susceptible because of their low oral bioavailability, but others such as atorvastatin and cerivastatin may also be affected.  High levels of HMG-CoA reductase inhibitory activity in plasma is associated with an increased risk of musculoskeletal toxicity.  Myopathy manifested as muscle pain and/or weakness associated with grossly elevated creatine kinase exceeding ten times the upper limit of normal has been reported occasionally.  Rhabdomyolysis has also occurred rarely, which may be accompanied by acute renal failure secondary to myoglobinuria and may result in death.  Clinically significant interactions have been reported with potent CYP450 3A4 inhibitors such as macrolide antibiotics, azole antifungals, protease inhibitors and nefazodone, and moderate inhibitors such as amiodarone, cyclosporine, danazol, diltiazem and verapamil.', NULL, 'Caution is recommended if atorvastatin, cerivastatin, lovastatin, simvastatin, or red yeast rice (which contains lovastatin) is prescribed with a CYP450 3A4 inhibitor.', NULL, 'Caution is recommended if atorvastatin, cerivastatin, lovastatin, simvastatin, or red yeast rice (which contains lovastatin) is prescribed with a CYP450 3A4 inhibitor.  It is advisable to monitor lipid levels and use the lowest effective statin dose.  All patients receiving statin therapy should be advised to promptly report any unexplained muscle pain, tenderness or weakness, particularly if accompanied by fever, malaise and/or dark colored urine.  Therapy should be discontinued if creatine kinase is markedly elevated in the absence of strenuous exercise or if myopathy is otherwise suspected or diagnosed.  Fluvastatin, pravastatin, and rosuvastatin are not expected to interact with CYP450 3A4 inhibitors.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/290860/', '[1] Spach DH, Bauwens JE, Clark CD, Burke WG "Rhabdomyolysis associated with lovastatin and erythromycin use." West J Med 154 (1991):  213-5[2] Ayanian JZ, Fuchs CS, Stone RM "Lovastatin and rhabdomyolysis." Ann Intern Med 109 (1988):  682-3[3] Corpier CL, Jones PH, Suki WN, et al. "Rhabdomyolysis and renal injury with lovastatin use. Report of two cases in cardiac transplant recipients." JAMA 260 (1988):  239-41[4] East C, Alivizatos PA, Grundy SM, Jones PH, Farmer JA "Rhabdomyolysis in patients receiving lovastatin after cardiac transplantation." N Engl J Med 318 (1988):  47-8[5] Norman DJ, Illingworth DR, Munson J, Hosenpud J "Myolysis and acute renal failure in a heart-transplant recipient receiving lovastatin." N Engl J Med 318 (1988):  46-7[6] Ayanian JZ, Fuchs CS, Stone RM "Lovastatin and rhabdomyolysis." Ann Intern Med 109 (1988):  682-3[7] "Product Information. Mevacor (lovastatin)." Merck & Co., Inc  (2002):[8] "Product Information. Zocor (simvastatin)." Merck & Co., Inc  (2001):[9] Dallaire M, Chamberland M "Severe rhabdomyolysis in a patient receiving lovastatin, danazol and doxycycline." Can Med Assoc J 150 (1994):  1991-4[10] Campana C, Iacona I, Regassi MB, et al. "Efficacy and pharmacokinetics of simvastatin in heart transplant recipients." Ann Pharmacother 29 (1995):  235-9[11] Lees RS, Lees AM "Rhabdomyolysis from the coadministration of lovastatin and the antifungal agent itraconazole." N Engl J Med 333 (1995):  664-5[12] Zhou LX, Finley DK, Hassell AE, Holtzman JL "Pharmacokinetic interact