INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (246592, 'Fosnetupitant', 'Copanlisib', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 and P-glycoprotein (P-gp) may increase the plasma concentrations of copanlisib, which is a substrate of both the isoenzyme and efflux transporter.', NULL, 'Caution is advised when copanlisib is used with CYP450 3A4 inhibitors.', NULL, 'Caution is advised when copanlisib is used with CYP450 3A4 inhibitors.  Patients should be monitored for potentially increased adverse effects such as nausea, vomiting, diarrhea, stomatitis, hyperglycemia, hypertension, noninfectious pneumonitis, cutaneous reactions (e.g., exfoliative dermatitis, maculopapular rash), anemia, neutropenia, thrombocytopenia, and infections.  Dosage adjustments for copanlisib may be necessary and should be done in accordance with the product labeling.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/290904/', '[1] "Product Information. Aliqopa (copanlisib)." Bayer Pharmaceutical Inc  (2017):', 1767365210);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (246593, 'Fosnetupitant', 'Crizotinib', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of crizotinib, which is primarily metabolized by the isoenzyme.', NULL, 'Caution is advised if crizotinib is prescribed in combination with moderate CYP450 3A4 inhibitors.', NULL, 'Caution is advised if crizotinib is prescribed in combination with moderate CYP450 3A4 inhibitors.  Regular monitoring of ECG and serum electrolytes, including potassium, magnesium and calcium, may be appropriate in some patients.  Treatment should be interrupted in patients who develop Grade 3 QTc prolongation until recovery to less than or equal to Grade 1, then resumed at 200 mg twice daily.  In case of recurrence of Grade 3 QTc prolongation, therapy should be withheld until recovery to less than or equal to Grade 1, then resumed at 250 mg once daily.  Permanently discontinue crizotinib therapy if Grade 3 QTc prolongation recurs or at any time during treatment if Grade 4 QTc prolongation develops.  Patients should be advised to seek medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, palpitations, irregular heartbeat, shortness of breath, or syncope.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/290906/', '[1] "Product Information. Xalkori (crizotinib)." Pfizer U.S. Pharmaceuticals Group  (2011):', 1767365210);
