INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (246867, 'Erythromycin', 'Brentuximab vedotin', 'Moderate', 'Coadministration with potent CYP450 3A4 inhibitors or P-glycoprotein (P-gp) inhibitors may increase the plasma concentrations of monomethyl auristatin E (MMAE), the microtubule-disrupting component of brentuximab vedotin.  MMAE is primarily metabolized by CYP450 3A4 and has been found in vitro to be a substrate of the P-gp efflux transporter.', NULL, 'Caution is advised when brentuximab is used with potent CYP450 3A4 inhibitors (e.', NULL, 'Caution is advised when brentuximab is used with potent CYP450 3A4 inhibitors (e.g., azole antifungal agents, clarithromycin, erythromycin, nefazodone, ritonavir, telithromycin) or P-gp inhibitors (e.g., protein kinase inhibitors, abiraterone, amiodarone, azithromycin, cyclosporine, dronedarone, ivacaftor) that are also potentially hepatotoxic.  Close monitoring for adverse effects including neutropenia, infection, peripheral neuropathy, and hepatotoxicity is recommended.  Patients should be advised to seek medical attention if they experience potential signs and symptoms of hepatotoxicity such as fever, rash, itching, anorexia, nausea, vomiting, fatigue, malaise, right upper quadrant pain, dark urine, pale stools, and jaundice.  Liver enzymes and bilirubin should be measured before and during treatment, especially in patients with underlying hepatic disease or marked baseline transaminase elevations.  Patients experiencing new, worsening, or recurrent hepatotoxicity may require a delay, change in dosage, or discontinuation of brentuximab vedotin in accordance with the product labeling.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/291221/', '[1] "Product Information. Xalkori (crizotinib)." Pfizer U.S. Pharmaceuticals Group  (2011):', 1767365210);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (246868, 'Brentuximab vedotin', 'Sulfisoxazole', 'Moderate', 'Coadministration of brentuximab vedotin with other agents known to induce hepatotoxicity may potentiate the risk of liver injury.', NULL, 'The risk of hepatic injury should be considered when brentuximab vedotin is used with other agents that are potentially hepatotoxic (e.', NULL, 'The risk of hepatic injury should be considered when brentuximab vedotin is used with other agents that are potentially hepatotoxic (e.g., acetaminophen; alcohol; androgens and anabolic steroids; antituberculous agents; azole antifungal agents; ACE inhibitors; cyclosporine (high dosages); disulfiram; endothelin receptor antagonists; interferons; ketolide and macrolide antibiotics; kinase inhibitors; minocycline; nonsteroidal anti-inflammatory agents; HIV reverse transcriptase inhibitors; proteasome inhibitors; retinoids; sulfonamides; tamoxifen; thiazolidinediones; tolvaptan; vincristine; zileuton; anticonvulsants such as carbamazepine, hydantoins, felbamate, and valproic acid; lipid-lowering medications such as fenofibrate, lomitapide, mipomersen, niacin, and statins; herbals and nutritional supplements such as black cohosh, chaparral, comfrey, DHEA, kava, pennyroyal oil, and red yeast rice).  Patients should be advised to seek medical attention if they experience potential signs and symptoms of hepatotoxicity such as fever, rash, itching, anorexia, nausea, vomiting, fatigue, malaise, right upper quadrant pain, dark urine, pale stools, and jaundice.  Liver enzymes and bilirubin should be measured before and during treatment, especially in patients with underlying hepatic disease or marked baseline transaminase elevations.  Patients experiencing new, worsening, or recurrent hepatotoxicity may require a delay, change in dosage, or discontinuation of brentuximab vedotin in accordance with the product labeling.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/291222/', '[1] "Product Information. Adcetris (brentuximab vedotin)." Seattle Genetics Inc  (2011):', 1767365210);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (246869, 'Dimethyl fumarate', 'Sulfisoxazole', 'Moderate', 'The use of fumaric acid esters in combination with medications that can cause nephrotoxicity (e.g., aminoglycosides, diuretics, nonsteroidal anti-inflammatory drugs, cyclosporine, lithium, methotrexate) may increase the potential for adverse renal reactions.', NULL, 'The use of fumaric acid esters in patients who receive concomitant treatment with potentially nephrotoxic agents, particularly for longer durations, has not been evaluated and should be approached with caution.', NULL, 'The use of fumaric acid esters in patients who receive concomitant treatment with potentially nephrotoxic agents, particularly for longer durations, has not been evaluated and should be approached with caution.  Assessment of renal function (e.g., serum creatinine, blood urea nitrogen, urinalysis) is recommended prior to initiating treatment with fumaric acid esters and as clinically indicated during treatment.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/291223/', '[1] "Product Information. Vumerity (diroximel fumarate)." Biogen Australia Pty Ltd  (2022):[2] "Product Information. Tecfidera (dimethyl fumarate)." Biogen Idec Inc  (2023):[3] "Product Information. Tecfidera (dimethyl fumarate)." Biogen Idec Ltd  (2022):[4] "Product Information. Skilarence (dimethyl fumarate)." Almirall Ltd  (2022):[5] "Product Information. Vumerity (diroximel fumarate)." Biogen Idec Ltd  (2022):[6] "Product Information. Vumerity (diroximel fumarate)." Biogen Idec Inc  (2023):[7] "Product Information. Furatec (dimethyl fumarate)." Pharmacor Pty Ltd  (2022):', 1767365