INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (246978, 'Fludeoxyglucose (18F)', 'Magnesium hydroxide', 'Moderate', 'Certain medications may interfere with the results of diagnostic studies using fludeoxyglucose F18 by increasing or decreasing uptake of the agent into tissues.', NULL, 'Clinicians should be aware of possible diagnostic interference associated with the following medications:  colony-stimulating factors may increase uptake in spleen and bone marrow; glucose-induced hyperglycemia may interfere with uptake; dopamine and insulin may decrease myocardial and tumor cell uptake; laxative-induced intestinal muscle activity and/or irritation may increase intestinal uptake.', NULL, 'Clinicians should be aware of possible diagnostic interference associated with the following medications:  colony-stimulating factors may increase uptake in spleen and bone marrow; glucose-induced hyperglycemia may interfere with uptake; dopamine and insulin may decrease myocardial and tumor cell uptake; laxative-induced intestinal muscle activity and/or irritation may increase intestinal uptake.', 'Distribution', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/291364/', '[1] "Multum Information Services, Inc. Expert Review Panel"[2] Cerner Multum, Inc. "Australian Product Information." O 0', 1767365210);
