INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1471741, 'Heparin', 'Oritavancin', 'Major', 'Use of oritavancin may interfere with the therapeutic monitoring of heparin therapy. Oritavancin can bind to and prevent the action of the phospholipid reagents that activate coagulation in commonly used laboratory coagulation tests.', 'DDInter', 1767317476, 'Use of intravenous unfractionated heparin is contraindicated for 120 hours (5 days) after oritavancin administration. For patients who require aPTT monitoring within 120 hours of oritavancin dosing, a non-phospholipid dependent coagulation test such as a Factor Xa (chromogenic) assay or an alternative anticoagulant not requiring aPTT monitoring may be considered.', 'Antagonism', 'Use of intravenous unfractionated heparin is contraindicated for 120 hours (5 days) after oritavancin administration.', NULL, NULL, 'Major', 'pharmacodynamic');
