INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1471743, 'Oritavancin', 'Lonafarnib', 'Moderate', 'Coadministration with inducers of CYP450 3A4 may decrease the plasma concentrations of lonafarnib. In vitro, lonafarnib has been shown to be primarily metabolized by CYP450 3A4 and, to a lesser extent, by CYP450 1A2, 2A6, 2C8, 2C9, 2C19, and 2E1.', 'DDInter', 1767317476, 'The potential for diminished pharmacologic effects of lonafarnib should be considered during coadministration with CYP450 3A4 inducers. Alternative treatments may be required if an interaction is suspected.', 'Metabolism', 'The potential for diminished pharmacologic effects of lonafarnib should be considered during coadministration with CYP450 3A4 inducers.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1471744, 'Oritavancin', 'Methysergide', 'Moderate', 'Coadministration with inducers of CYP450 3A4 may decrease the plasma concentrations of ergot alkaloids, which are substrates of the isoenzyme.', 'DDInter', 1767317476, 'The potential for diminished pharmacologic effects of ergot alkaloids should be considered during coadministration with CYP450 3A4 inducers. Alternative treatments may be required if an interaction is suspected.', 'Metabolism', 'The potential for diminished pharmacologic effects of ergot alkaloids should be considered during coadministration with CYP450 3A4 inducers.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1471745, 'Osilodrostat', 'Oritavancin', 'Moderate', 'Coadministration with inducers of CYP450 3A4 and/or 2B6 may decrease the plasma concentrations of osilodrostat, which is partially metabolized by these isoenzymes. According to the product labeling, multiple CYP450 isoenzymes (CYP450 3A4, 2B6, and 2D6) and UDP-glucuronosyltransferases contribute to osilodrostat metabolism, and no single pathway contributes greater than 25% to the total clearance. Some of the known CYP450 3A4 and 2B6 inducers also induce UDP-glucuronosyltransferases.', 'DDInter', 1767317476, 'The potential for diminished pharmacologic effects of osilodrostat should be considered during concomitant use of a CYP450 3A4 and/or 2B6 inducer. An increase in the dosage of osilodrostat may be required. Dosage adjustments should be based on clinical response and tolerance. Patients should have regular monitoring of 24-hour urine free cortisol and serum or plasma cortisol during treatment, as well as regular evaluations of their signs and symptoms. A reduction in dosage of osilodrostat may be required if the CYP450 3A4 and/or 2B6 inducer is discontinued during treatment with osilodrostat.', 'Metabolism', 'The potential for diminished pharmacologic effects of osilodrostat should be considered during concomitant use of a CYP450 3A4 and/or 2B6 inducer.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1471746, 'Pemigatinib', 'Oritavancin', 'Moderate', 'Coadministration with inducers of CYP450 3A4 may decrease the plasma concentrations of pemigatinib, which is primarily metabolized by the isoenzyme in vitro.', 'DDInter', 1767317476, 'The potential for diminished pharmacologic effects of pemigatinib should be considered during coadministration with CYP450 3A4 inducers. Alternative treatments may be required if an interaction is suspected.', 'Metabolism', 'The potential for diminished pharmacologic effects of pemigatinib should be considered during coadministration with CYP450 3A4 inducers.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1471747, 'Pimavanserin', 'Oritavancin', 'Moderate', 'Coadministration with inducers of CYP450 3A4 may decrease the plasma concentrations of pimavanserin, which is primarily metabolized by the isoenzyme.', 'DDInter', 1767317476, 'The possibility of diminished therapeutic response to pimavanserin should be considered during coadministration with CYP450 3A4 inducers. The manufacturer recommends coadministration with strong or moderate CYP450 3A4 inducers be avoided.', 'Metabolism', 'The possibility of diminished therapeutic response to pimavanserin should be considered during coadministration with CYP450 3A4 inducers.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1471748, 'Praziquantel', 'Oritavancin', 'Moderate', 'Coadministration with inducers of CYP450 3A4 may decrease the plasma concentrations of praziquantel, which is a substrate of the isoenzyme.', 'DDInter', 1767317476, 'Concomitant use of praziquantel with CYP450 3A4 inducers should generally be avoided, since therapeutically effective blood levels of praziquantel may not be achieved. Alternative agents for schistosomiasis should be considered whenever possible in patients receiving treatment with a CYP450 3A4 inducer. Otherwise, clinical response to praziquantel should be closely monitored.', 'Metabolism', 'Concomitant use of praziquantel with CYP450 3A4 inducers should generally be avoided, since therapeutically effective blood levels of praziquantel may not be achieved.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1471749, 'Relugolix', 'Oritavancin', 'Minor', 'Coadministration with lone inducers of CYP450 3A4 is unlikely to decrease the plasma concentrations of relugolix to a clinically significant extent.', 'DDInter', 1767317476, '-', 'Others', '-', NULL, NULL, 'Minor', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1471750, 'Ribociclib', 'Oritavancin', 'Moderate', 'Coadministration with inducers of CYP450 3A4 may decrease the plasma concentrations of ribociclib, which is a substrate of the isoenzyme.', 'DDInter', 1767317476, 'The potential for diminished therapeutic effects of ribociclib should be considered when prescribed with inducers of CYP450 3A4, particularly moderate inducers including efavirenz and nevirapine. Pharmacologic effects of ribociclib should be monitored more closely whenever an inducer is added to or withdrawn from therapy. Alternative treatment may be required if an interaction is suspected.', 'Metabolism', 'The potential for diminished therapeutic effects of ribociclib should be considered when prescribed with inducers of CYP450 3A4, particularly moderate inducers including efavirenz and nevirapine.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1471751, 'Ripretinib', 'Oritavancin', 'Moderate', 'Coadministration with inducers of CYP450 3A4 may decrease the plasma concentrations and anti-tumor activities of ripretinib and its active metabolite (DP-5439). The proposed mechanism is induction of CYP450 3A4, the isoenzyme responsible for the metabolic clearance of ripretinib and DP-5439.', 'DDInter', 1767317476, 'Until more information is available, caution is advised when ripretinib is used with a CYP450 3A4 inducer. Clinical and laboratory monitoring should be considered whenever a CYP450 3A4 inducer is added to or withdrawn from therapy, and the ripretinib dosage adjusted as necessary. Patients should be monitored for loss of anti-tumor activity.', 'Metabolism', 'Until more information is available, caution is advised when ripretinib is used with a CYP450 3A4 inducer.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1471752, 'Selpercatinib', 'Oritavancin', 'Moderate', 'Coadministration with inducers of CYP450 3A4 may decrease the plasma concentrations of selpercatinib, which is primarily metabolized by the isoenzyme. When selpercatinib was coadministered with multiple doses of rifampin, a potent CYP450 3A4 inducer, selpercatinib peak plasma concentration (Cmax) and systemic exposure (AUC) decreased by 70% and 87%, respectively.', 'DDInter', 1767317476, 'The potential for diminished pharmacologic effects of selpercatinib should be considered during coadministration with CYP450 3A4 inducers. Alternative treatments may be required if an interaction is suspected.', 'Metabolism', 'The potential for diminished pharmacologic effects of selpercatinib should be considered during coadministration with CYP450 3A4 inducers.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1471753, 'Selumetinib', 'Oritavancin', 'Moderate', 'Coadministration with inducers of CYP450 3A4 may decrease the plasma concentrations of selumetinib, which is primarily metabolized by CYP450 3A4 and to a lesser extent by CYP450 2C19, 1A2, 2C9, 2E1, and 3A5. Selumetinib also undergoes glucuronidation by UGT1A1 and UGT1A3.', 'DDInter', 1767317476, 'The potential for diminished pharmacologic effects of selumetinib should be considered during coadministration with CYP450 3A4 inducers. Alternative treatments may be required if an interaction is suspected.', 'Metabolism', 'The potential for diminished pharmacologic effects of selumetinib should be considered during coadministration with CYP450 3A4 inducers.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1471754, 'Silodosin', 'Oritavancin', 'Moderate', 'Coadministration with inducers of CYP450 3A4 and/or P-glycoprotein (P-gp) may decrease the plasma concentrations of silodosin, which is a substrate of both the isoenzyme and efflux transporter.', 'DDInter', 1767317476, 'The potential for diminished pharmacologic effects of silodosin should be considered during coadministration with CYP450 3A4 and/or P-gp inducers. Alternative treatments may be required if an interaction is suspected.', 'Metabolism', 'The potential for diminished pharmacologic effects of silodosin should be considered during coadministration with CYP450 3A4 and/or P-gp inducers.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1471755, 'Sorafenib', 'Oritavancin', 'Moderate', 'Coadministration with inducers of CYP450 3A4 may decrease the plasma concentrations of sorafenib, which is partially metabolized by the isoenzyme.', 'DDInter', 1767317476, 'The potential for diminished pharmacologic effects of sorafenib should be considered during coadministration with CYP450 3A4 inducers.', 'Metabolism', 'The potential for diminished pharmacologic effects of sorafenib should be considered during coadministration with CYP450 3A4 inducers.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1471756, 'Sunitinib', 'Oritavancin', 'Moderate', 'Coadministration with inducers of CYP450 3A4 may decrease the plasma concentrations of sunitinib and its pharmacologically active metabolite, both of which are substrates of the isoenzyme.', 'DDInter', 1767317476, 'The potential for diminished pharmacologic effects of sunitinib should be considered during coadministration with CYP450 3A4 inducers. Alternative treatments or dose increase for sunitinib may be required if an interaction is suspected.', 'Metabolism', 'The potential for diminished pharmacologic effects of sunitinib should be considered during coadministration with CYP450 3A4 inducers.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1471757, 'Tazemetostat', 'Oritavancin', 'Moderate', 'Coadministration with inducers of CYP450 3A4 may decrease the plasma concentrations of tazemetostat, which is primarily metabolized by the isoenzyme. This may lead to reduced efficacy of tazemetostat.', 'DDInter', 1767317476, 'Caution and clinical monitoring is recommended if tazemetostat is to be used concomitantly with CYP450 3A4 inducers.', 'Metabolism', 'Caution and clinical monitoring is recommended if tazemetostat is to be used concomitantly with CYP450 3A4 inducers.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1471758, 'Temsirolimus', 'Oritavancin', 'Moderate', 'Coadministration of temsirolimus with inducers of CYP450 3A4 may decrease the plasma concentrations of sirolimus, a major active metabolite of temsirolimus and known substrate of CYP450 3A4.', 'DDInter', 1767317476, 'In the setting of mantle cell lymphoma, concomitant use of temsirolimus with inducers of CYP450 3A4 should generally be avoided. When used for the treatment of renal cell carcinoma, some authorities advise a dose increase from the recommended 25 mg IV once weekly to 50 mg IV once weekly depending on patient tolerability. The dosage should be reduced to the normally recommended dose following discontinuation of the potent CYP450 3A4 inducer.', 'Metabolism', 'In the setting of mantle cell lymphoma, concomitant use of temsirolimus with inducers of CYP450 3A4 should generally be avoided.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1471759, 'Ticagrelor', 'Oritavancin', 'Moderate', 'Coadministration with inducers of CYP450 3A4 may decrease the plasma concentrations of ticagrelor, which is primarily metabolized by the isoenzyme.', 'DDInter', 1767317476, 'Concomitant use of ticagrelor with CYP450 3A4 inducers should generally be avoided. Otherwise, caution is advised, and patients should be closely monitored for diminished clinical response to ticagrelor therapy. Alternative treatment may be required if an interaction is suspected.', 'Metabolism', 'Concomitant use of ticagrelor with CYP450 3A4 inducers should generally be avoided.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1471760, 'Toremifene', 'Oritavancin', 'Moderate', 'Coadministration with inducers of CYP450 3A4 may decrease the plasma concentrations of toremifene. According to the product labeling, toremifene is primarily metabolized by CYP450 3A4 to N-demethyltoremifene, an antiestrogenic metabolite with weak in vivo antitumor potency but whose serum concentrations are 2 to 4 times higher than those of toremifene at steady state. The extent to which other, less potent CYP450 3A4 inducers may affect toremifene is unknown.', 'DDInter', 1767317476, 'The potential for diminished pharmacologic effects of toremifene should be considered during coadministration with CYP450 3A4 inducers. Alternative treatments or a dose adjustment of toremifene may be required if an interaction is suspected.', 'Metabolism', 'The potential for diminished pharmacologic effects of toremifene should be considered during coadministration with CYP450 3A4 inducers.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1471761, 'Tucatinib', 'Oritavancin', 'Moderate', 'Coadministration with CYP450 3A4 inducers may decrease the plasma concentrations of tucatinib, which is primarily metabolized by CYP450 2C8 and to a lesser extent by CYP450 3A4.', 'DDInter', 1767317476, 'The potential for diminished pharmacologic effects of tucatinib should be considered during coadministration with CYP450 3A4 inducers. Alternative treatments may be required if an interaction is suspected.', 'Metabolism', 'The potential for diminished pharmacologic effects of tucatinib should be considered during coadministration with CYP450 3A4 inducers.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1471762, 'Typhoid vaccine (live)', 'Oritavancin', 'Major', 'INTERVAL: The use of live, attenuated oral typhoid vaccine with antibacterial sulfonamides or other antibiotics may result in a diminished immunologic response to the vaccine. Some antibiotics may be active against the vaccine strain of Salmonella typhi (Ty21a), thereby preventing a sufficient degree of multiplication to occur in order to induce a protective immune response.', 'DDInter', 1767317476, 'Live, attenuated oral typhoid vaccine should not be administered during and for at least 3 days before and after treatment with antibacterial sulfonamides or other antibiotics. A longer interval should be considered following treatment with long-acting antibiotics (e.g., azithromycin). If malaria prophylaxis is needed, the same 3-day interval at the minimum should be observed between antimalarials and the vaccine. However, chloroquine, mefloquine, and pyrimethamine-sulfadoxine may be given concomitantly with the vaccine. Alternatively, parenteral typhoid vaccine (typhoid Vi polysaccharide vaccine) may be considered if coadministration with antibacterial agents is required.', 'Others', 'Live, attenuated oral typhoid vaccine should not be administered during and for at least 3 days before and after treatment with antibacterial sulfonamides or other antibiotics.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1471763, 'Ubrogepant', 'Oritavancin', 'Moderate', 'Coadministration with moderate or weak inducers of CYP450 3A4 may decrease the plasma concentrations of ubrogepant, which is primarily metabolized by the isoenzyme. Dedicated drug interaction studies have not been conducted to assess concomitant use of ubrogepant with moderate or weak CYP450 3A4 inducers.', 'DDInter', 1767317476, 'The manufacturer recommends an initial ubrogepant dose of 100 mg when coadministered with moderate or weak CYP450 3A4 inducers. If needed, a second 100 mg dose of ubrogepant may be administered at least 2 hours after the initial dose.', 'Metabolism', 'The manufacturer recommends an initial ubrogepant dose of 100 mg when coadministered with moderate or weak CYP450 3A4 inducers.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1471764, 'Vemurafenib', 'Oritavancin', 'Moderate', 'Coadministration with inducers of CYP450 3A4 may decrease the plasma concentrations of vemurafenib, which has been shown in vitro to be a substrate of the isoenzyme.', 'DDInter', 1767317476, 'The potential for diminished pharmacologic effects of vemurafenib should be considered during coadministration with CYP450 3A4 inducers. Alternative treatments or a dose adjustment of vemurafenib may be required if an interaction is suspected.', 'Metabolism', 'The potential for diminished pharmacologic effects of vemurafenib should be considered during coadministration with CYP450 3A4 inducers.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1471765, 'Zanubrutinib', 'Oritavancin', 'Moderate', 'Coadministration with inducers of CYP450 3A4 may decrease the plasma concentrations of zanubrutinib, which is primarily metabolized by the isoenzyme.', 'DDInter', 1767317476, 'The potential for diminished pharmacologic effects of zanubrutinib should be considered during coadministration with CYP450 3A4 inducers. Alternative treatments may be required if an interaction is suspected.', 'Metabolism', 'The potential for diminished pharmacologic effects of zanubrutinib should be considered during coadministration with CYP450 3A4 inducers.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1471766, 'Acetohexamide', 'Orlistat', 'Moderate', 'Since weight loss may affect glycemic control and increase the risk of hypoglycemia in type 2 diabetes mellitus, patients receiving anorectic drugs may require periodic adjustments of their antidiabetic medications.', 'DDInter', 1767317476, 'Blood glucose should be closely monitored during weight loss treatment, and appropriate changes made to the antidiabetic drug regimen as needed. Patients should be apprised of the risk of hypoglycemia and be alert to potential signs and symptoms such as headache, dizziness, drowsiness, nervousness, confusion, tremor, hunger, weakness, perspiration, palpitation, and tachycardia.', 'Synergism', 'Blood glucose should be closely monitored during weight loss treatment, and appropriate changes made to the antidiabetic drug regimen as needed.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1471767, 'Amprenavir', 'Orlistat', 'Moderate', 'Orlistat may theoretically reduce the gastrointestinal absorption and therapeutic efficacy of antiretroviral agents. The mechanism may involve retention of lipophilic medicines in the gastrointestinal tract or reduced gastrointestinal tract transit time.', 'DDInter', 1767317476, 'The potential reduction in antiretroviral efficacy should be considered before initiating treatment with orlistat. Patients who take antiretroviral medications should be advised to consult their physician before using the non-prescription brand of orlistat.', 'Absorption', 'The potential reduction in antiretroviral efficacy should be considered before initiating treatment with orlistat.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1471768, 'Anisindione', 'Orlistat', 'Moderate', 'Coadministration with orlistat may potentiate the hypoprothrombinemic effect of oral anticoagulants. The proposed mechanism is orlistat reduction of the absorption of vitamin K, a fat-soluble vitamin and cofactor in the synthesis of blood clotting factors that are inhibited by oral anticoagulants. In addition, dietary changes often prompted by orlistat use may also alter vitamin K intake and contribute to the interaction.', 'DDInter', 1767317476, 'Patients should be closely monitored during concomitant therapy with oral anticoagulants and orlistat. The INR should be checked frequently and anticoagulant dosage adjusted accordingly, particularly following initiation or discontinuation of orlistat in patients who are stabilized on their anticoagulant regimen. Patients should be advised to promptly report any signs of bleeding to their physician, including pain, swelling, headache, dizziness, weakness, prolonged bleeding from cuts, increased menstrual flow, vaginal bleeding, nosebleeds, bleeding of gums from brushing, unusual bleeding or bruising, red or brown urine, or red or black stools.', 'Absorption', 'Patients should be closely monitored during concomitant therapy with oral anticoagulants and orlistat.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1471769, 'Bedaquiline', 'Orlistat', 'Moderate', 'Coadministration of bedaquiline with other agents known to induce hepatotoxicity may potentiate the risk of liver injury.', 'DDInter', 1767317476, 'The use of bedaquiline with other potentially hepatotoxic agents should be avoided whenever possible (e.g., acetaminophen; alcohol; androgens and anabolic steroids; other antituberculous agents; azole antifungal agents; ACE inhibitors; disulfiram; endothelin receptor antagonists; ketolide and macrolide antibiotics; interferons; kinase inhibitors; minocycline; nonsteroidal anti-inflammatory agents; nucleoside reverse transcriptase inhibitors; proteasome inhibitors; retinoids; sulfonamides; tamoxifen; thiazolidinediones; tolvaptan; vincristine; zileuton; anticonvulsants such as carbamazepine, hydantoins, felbamate, and valproic acid; lipid-lowering medications such as fenofibrate, lomitapide, mipomersen, niacin, and statins; herbals and nutritional supplements such as black cohosh, chaparral, comfrey, DHEA, kava, pennyroyal oil, and red yeast rice), especially in patients with diminished hepatic reserve. Patients treated with bedaquiline should have serum ALT, AST, alkaline phosphatase, and bilirubin monitored at baseline and monthly during treatment, or as often as needed. An increase of serum aminotransferases to greater than 3 times ULN should be followed by repeat testing within 48 hours. Patients should be advised to seek medical attention if they experience potential signs and symptoms of hepatotoxicity such as fever, rash, itching, anorexia, nausea, vomiting, fatigue, malaise, right upper quadrant pain, dark urine, pale stools, and jaundice. Discontinue bedaquiline if aminotransferase elevations are accompanied by total bilirubin elevation greater than 2 times ULN, aminotransferase elevations are greater than 8 times ULN, or aminotransferase elevations persist beyond 2 weeks.', 'Synergism', 'The use of bedaquiline with other potentially hepatotoxic agents should be avoided whenever possible (e.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1471770, 'Boceprevir', 'Orlistat', 'Moderate', 'Orlistat may theoretically reduce the gastrointestinal absorption and therapeutic efficacy of antiretroviral agents. The mechanism may involve retention of lipophilic medicines in the gastrointestinal tract or reduced gastrointestinal tract transit time.', 'DDInter', 1767317476, 'The potential reduction in antiretroviral efficacy should be considered before initiating treatment with orlistat. Patients who take antiretroviral medications should be advised to consult their physician before using the non-prescription brand of orlistat.', 'Absorption', 'The potential reduction in antiretroviral efficacy should be considered before initiating treatment with orlistat.', NULL, NULL, 'Moderate', 'pharmacodynamic');
