INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1471782, 'Glycerol phenylbutyrate', 'Orlistat', 'Moderate', 'Concomitant use of glycerol phenylbutyrate with medicines known to inhibit pancreatic lipases may theoretically lead to reduced intestinal absorption of its active moiety phenylbutyrate, and hence reduced control of plasma ammonia levels. After oral ingestion, pancreatic lipases hydrolyse glycerol phenylbutyrate in the small intestine to glycerol and phenylbutyrate, allowing the absorption of phenylbutyrate and its subsequent conversion to the active metabolite, phenylacetic acid, by beta-oxidation. Clinical data are not available.', 'DDInter', 1767317476, 'Caution and close monitoring of ammonia levels is recommended if glycerol phenylbutyrate is to be administered with a gastrointestinal lipase inhibitor such as orlistat. The dose of glycerol phenylbutyrate should be adjusted as necessary per local guidelines.', 'Absorption', 'Caution and close monitoring of ammonia levels is recommended if glycerol phenylbutyrate is to be administered with a gastrointestinal lipase inhibitor such as orlistat.', NULL, NULL, 'Moderate', 'pharmacodynamic');
