INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1472114, 'Ibutilide', 'Osimertinib', 'Major', 'Like other class III antiarrhythmic agents, ibutilide can cause dose-related QT interval prolongation. Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death.', 'DDInter', 1767317476, 'Caution is recommended if ibutilide is used in combination with other drugs that can prolong the QT interval. Ibutilide should only be administered in a setting of continuous ECG monitoring and by personnel trained in identification and treatment of acute ventricular arrhythmias, particularly polymorphic ventricular tachycardia such as torsade de pointes.', 'Synergism', 'Caution is recommended if ibutilide is used in combination with other drugs that can prolong the QT interval.', NULL, NULL, 'Major', 'pharmacodynamic');
