INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1472134, 'Osimertinib', 'Lyme disease vaccine (recombinant OspA)', 'Moderate', 'The administration of Lyme disease vaccine to immunosuppressed patients may be associated with a diminished or suboptimal immunologic response due to antibody inhibition. Such patients may include those who have recently received or are receiving immunosuppressive agents, antilymphocyte globulins, alkylating agents, antimetabolites, radiation, some antirheumatic agents, high dosages of corticosteroids or adrenocorticotropic agents, or long-term topical or inhaled corticosteroids.', 'DDInter', 1767317476, 'The manufacturer suggests considering deferral of vaccination against Lyme disease until 3 months after discontinuation of immunosuppressive therapy. Vaccines may generally be administered to patients receiving corticosteroids as replacement therapy (e.g., for Addison''s disease).', 'Others', 'The manufacturer suggests considering deferral of vaccination against Lyme disease until 3 months after discontinuation of immunosuppressive therapy.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1472135, 'Osimertinib', 'Magnesium citrate', 'Moderate', 'Bowel cleansing as well as overuse of certain laxatives may cause electrolyte loss and increase the risk of torsade de pointes ventricular arrhythmia in patients treated with drugs that prolong the QT interval. Electrolyte disturbances including hypokalemia and hypomagnesemia have been reported with laxative abuse and are known risk factors for torsade de pointes associated with QT interval prolongation.', 'DDInter', 1767317476, 'Patients treated with drugs that prolong the QT interval should exercise caution when self-medicating with laxatives. The recommended dosage and duration of use should not be exceeded. Patients should be advised to seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope.', 'Synergism', 'Patients treated with drugs that prolong the QT interval should exercise caution when self-medicating with laxatives.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1472136, 'Osimertinib', 'Magnesium hydroxide', 'Moderate', 'Bowel cleansing as well as overuse of certain laxatives may cause electrolyte loss and increase the risk of torsade de pointes ventricular arrhythmia in patients treated with drugs that prolong the QT interval. Electrolyte disturbances including hypokalemia and hypomagnesemia have been reported with laxative abuse and are known risk factors for torsade de pointes associated with QT interval prolongation.', 'DDInter', 1767317476, 'Patients treated with drugs that prolong the QT interval should exercise caution when self-medicating with laxatives. The recommended dosage and duration of use should not be exceeded. Patients should be advised to seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope.', 'Synergism', 'Patients treated with drugs that prolong the QT interval should exercise caution when self-medicating with laxatives.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1472137, 'Osimertinib', 'Measles virus vaccine live attenuated', 'Major', 'The administration of live, attenuated viral or bacterial vaccines during immunosuppressant or intense antineoplastic therapy may be associated with a risk of disseminated infection due to enhanced replication of vaccine virus or bacteria in the presence of diminished immune competence. Patients may be immunosuppressed if they have recently received or are receiving alkylating agents, antimetabolites, radiation, some antirheumatic agents, high dosages of corticosteroids or adrenocorticotropic agents, or long-term topical or inhaled corticosteroids. These patients may also have increased adverse reactions and decreased or suboptimal immunologic response to vaccines.', 'DDInter', 1767317476, 'In general, live attenuated vaccines should not be used in patients receiving immunosuppressive therapy or cancer chemotherapy. Vaccination should be deferred until after such therapy is discontinued and immune function has been restored, usually 4 to 12 weeks after stopping immunosuppressive therapy. A longer waiting period may be necessary following treatment with agents that have a prolonged elimination half-life (e.g., leflunomide, teriflunomide). Current local immunization guidelines should be consulted for recommendations.', 'Others', 'In general, live attenuated vaccines should not be used in patients receiving immunosuppressive therapy or cancer chemotherapy.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1472138, 'Osimertinib', 'Melatonin', 'Moderate', 'Coadministration with osimertinib may decrease the plasma concentrations of drugs that are primarily metabolized by CYP450 1A2. In vitro, osimertinib has been shown to be an inducer of the isoenzyme.', 'DDInter', 1767317476, 'Caution is advised when osimertinib is prescribed with drugs that undergo metabolism by CYP450 1A2. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever osimertinib is added to or withdrawn from therapy. Sensitive CYP450 1A2 substrates or substrates with narrow therapeutic ranges (e.g., theophylline) should preferably be avoided with osimertinib, if possible.', 'Metabolism', 'Caution is advised when osimertinib is prescribed with drugs that undergo metabolism by CYP450 1A2.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1472139, 'Osimertinib', 'Mesoridazine', 'Major', 'Mesoridazine can cause dose-related prolongation of the QT interval. Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death. Mesoridazine overdosage has been associated with ventricular arrhythmias and death. The extent of drug-induced QT prolongation is dependent on the particular drug(s) involved and dosage(s) of the drug(s). In addition, certain agents with anticholinergic properties (e.g., sedating antihistamines; antispasmodics; neuroleptics; phenothiazines; skeletal muscle relaxants; tricyclic antidepressants; disopyramide) may have additive parasympatholytic and central nervous system-depressant effects when used in combination with mesoridazine. Excessive parasympatholytic effects may include paralytic ileus, hyperthermia, mydriasis, blurred vision, tachycardia, urinary retention, psychosis, and seizures.', 'DDInter', 1767317476, 'Coadministration of mesoridazine with other drugs that can prolong the QT interval is considered contraindicated.', 'Synergism', 'Coadministration of mesoridazine with other drugs that can prolong the QT interval is considered contraindicated.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1472140, 'Osimertinib', 'Methotrimeprazine', 'Major', 'Osimertinib may cause dose-related prolongation of the QT interval. Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death.', 'DDInter', 1767317476, 'Caution is recommended if osimertinib is used in combination with other drugs that can prolong the QT interval. ECG and serum electrolytes, including potassium, magnesium and calcium, should be monitored before starting osimertinib therapy and periodically during treatment.', 'Synergism', 'Caution is recommended if osimertinib is used in combination with other drugs that can prolong the QT interval.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1472141, 'Osimertinib', 'Methysergide', 'Moderate', 'Coadministration with osimertinib may alter the plasma concentrations of drugs that are primarily metabolized by CYP450 3A4. In vitro, osimertinib has been shown to be a competitive inhibitor as well as inducer of CYP450 3A4.', 'DDInter', 1767317476, 'Concomitant use of osimertinib and sensitive CYP450 3A4 substrates or substrates with narrow therapeutic ranges should generally be avoided. These drugs include, but are not limited to: alfentanil, cisapride, colchicine, cyclosporine, fentanyl, ivacaftor, lovastatin, simvastatin, oral midazolam, triazolam, pimozide, quinidine, sildenafil, ergot derivatives, macrolide immunosuppressants, and vinca alkaloids.', 'Metabolism', 'Concomitant use of osimertinib and sensitive CYP450 3A4 substrates or substrates with narrow therapeutic ranges should generally be avoided.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1472142, 'Osimertinib', 'Mineral oil', 'Moderate', 'Bowel cleansing as well as overuse of certain laxatives may cause electrolyte loss and increase the risk of torsade de pointes ventricular arrhythmia in patients treated with drugs that prolong the QT interval. Electrolyte disturbances including hypokalemia and hypomagnesemia have been reported with laxative abuse and are known risk factors for torsade de pointes associated with QT interval prolongation.', 'DDInter', 1767317476, 'Patients treated with drugs that prolong the QT interval should exercise caution when self-medicating with laxatives. The recommended dosage and duration of use should not be exceeded. Patients should be advised to seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope.', 'Synergism', 'Patients treated with drugs that prolong the QT interval should exercise caution when self-medicating with laxatives.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1472143, 'Osimertinib', 'Mumps virus strain B level jeryl lynn live antigen', 'Major', 'The administration of live, attenuated viral or bacterial vaccines during immunosuppressant or intense antineoplastic therapy may be associated with a risk of disseminated infection due to enhanced replication of vaccine virus or bacteria in the presence of diminished immune competence. Patients may be immunosuppressed if they have recently received or are receiving alkylating agents, antimetabolites, radiation, some antirheumatic agents, high dosages of corticosteroids or adrenocorticotropic agents, or long-term topical or inhaled corticosteroids. These patients may also have increased adverse reactions and decreased or suboptimal immunologic response to vaccines.', 'DDInter', 1767317476, 'In general, live attenuated vaccines should not be used in patients receiving immunosuppressive therapy or cancer chemotherapy. Vaccination should be deferred until after such therapy is discontinued and immune function has been restored, usually 4 to 12 weeks after stopping immunosuppressive therapy. A longer waiting period may be necessary following treatment with agents that have a prolonged elimination half-life (e.g., leflunomide, teriflunomide). Current local immunization guidelines should be consulted for recommendations.', 'Others', 'In general, live attenuated vaccines should not be used in patients receiving immunosuppressive therapy or cancer chemotherapy.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1472144, 'Osimertinib', 'Nafcillin', 'Moderate', 'Coadministration with inducers of CYP450 3A4 may decrease the plasma concentrations of osimertinib, which has been shown in vitro to be primarily metabolized by the isoenzyme. Reduced therapeutic efficacy of osimertinib may occur. However, no pharmacokinetic data are available from drug interaction studies.', 'DDInter', 1767317476, 'The potential for diminished pharmacologic effects of osimertinib should be considered during coadministration with CYP450 3A4 inducers. Alternative treatments may be required if an interaction is suspected.', 'Metabolism', 'The potential for diminished pharmacologic effects of osimertinib should be considered during coadministration with CYP450 3A4 inducers.', NULL, NULL, 'Moderate', 'pharmacodynamic');
