INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (247091, 'Telmisartan', 'Monomethyl fumarate', 'Moderate', 'The use of fumaric acid esters in combination with medications that can cause nephrotoxicity (e.g., aminoglycosides, diuretics, nonsteroidal anti-inflammatory drugs, cyclosporine, lithium, methotrexate) may increase the potential for adverse renal reactions.', NULL, 'The use of fumaric acid esters in patients who receive concomitant treatment with potentially nephrotoxic agents, particularly for longer durations, has not been evaluated and should be approached with caution.', NULL, 'The use of fumaric acid esters in patients who receive concomitant treatment with potentially nephrotoxic agents, particularly for longer durations, has not been evaluated and should be approached with caution.  Assessment of renal function (e.g., serum creatinine, blood urea nitrogen, urinalysis) is recommended prior to initiating treatment with fumaric acid esters and as clinically indicated during treatment.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/291732/', '[1] "Product Information. Vumerity (diroximel fumarate)." Biogen Australia Pty Ltd  (2022):[2] "Product Information. Tecfidera (dimethyl fumarate)." Biogen Idec Inc  (2023):[3] "Product Information. Tecfidera (dimethyl fumarate)." Biogen Idec Ltd  (2022):[4] "Product Information. Skilarence (dimethyl fumarate)." Almirall Ltd  (2022):[5] "Product Information. Vumerity (diroximel fumarate)." Biogen Idec Ltd  (2022):[6] "Product Information. Vumerity (diroximel fumarate)." Biogen Idec Inc  (2023):[7] "Product Information. Furatec (dimethyl fumarate)." Pharmacor Pty Ltd  (2022):', 1767365210);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (247092, 'Telmisartan', 'Patiromer', 'Moderate', 'Patiromer can bind to some orally administered drugs, which may decrease their gastrointestinal absorption and reduce their effectiveness.  According to the manufacturer, in vitro studies observed potentially clinically significant binding between patiromer and telmisartan, bisoprolol, carvedilol, nebivolol, mycophenolate mofetil, quinidine, and thiamine.', NULL, 'Patiromer should be administered at least 3 hours before or 3 hours after other oral medications, particularly ciprofloxacin, levothyroxine, metformin, telmisartan, bisoprolol, carvedilol, nebivolol, mycophenolate mofetil, quinidine, and thiamine.', NULL, 'Patiromer should be administered at least 3 hours before or 3 hours after other oral medications, particularly ciprofloxacin, levothyroxine, metformin, telmisartan, bisoprolol, carvedilol, nebivolol, mycophenolate mofetil, quinidine, and thiamine.  Alternatives to patiromer or the other medications should be considered if adequate dosing separation is not possible.  Otherwise, clinical response and/or blood levels should be monitored where possible.', 'Absorption', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/291733/', '[1] "Product Information. Veltassa (patiromer)." Relypsa, Inc.  (2015):[2] "Product Information. Veltassa (patiromer)." Vifor Pharma Pty Ltd  (2023):[3] "Product Information. Veltassa (patiromer)." Vifor Fresenius Medical Care Renal Pharma UK Ltd  (2022):', 1767365210);
