INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (247115, 'Clarithromycin', 'Brentuximab vedotin', 'Moderate', 'Coadministration with potent CYP450 3A4 inhibitors or P-glycoprotein (P-gp) inhibitors may increase the plasma concentrations of monomethyl auristatin E (MMAE), the microtubule-disrupting component of brentuximab vedotin.  MMAE is primarily metabolized by CYP450 3A4 and has been found in vitro to be a substrate of the P-gp efflux transporter.', NULL, 'Caution is advised when brentuximab is used with potent CYP450 3A4 inhibitors (e.', NULL, 'Caution is advised when brentuximab is used with potent CYP450 3A4 inhibitors (e.g., azole antifungal agents, clarithromycin, erythromycin, nefazodone, ritonavir, telithromycin) or P-gp inhibitors (e.g., protein kinase inhibitors, abiraterone, amiodarone, azithromycin, cyclosporine, dronedarone, ivacaftor) that are also potentially hepatotoxic.  Close monitoring for adverse effects including neutropenia, infection, peripheral neuropathy, and hepatotoxicity is recommended.  Patients should be advised to seek medical attention if they experience potential signs and symptoms of hepatotoxicity such as fever, rash, itching, anorexia, nausea, vomiting, fatigue, malaise, right upper quadrant pain, dark urine, pale stools, and jaundice.  Liver enzymes and bilirubin should be measured before and during treatment, especially in patients with underlying hepatic disease or marked baseline transaminase elevations.  Patients experiencing new, worsening, or recurrent hepatotoxicity may require a delay, change in dosage, or discontinuation of brentuximab vedotin in accordance with the product labeling.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/291953/', '[1] "Product Information. Xalkori (crizotinib)." Pfizer U.S. Pharmaceuticals Group  (2011):', 1767365210);
