INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (247152, 'Aminophylline', 'Vilanterol', 'Moderate', 'Concomitant use of beta-2 adrenergic agonists with theophylline may increase the risk and/or severity of hypokalemia and adverse cardiovascular effects such as palpitation, tachycardia, and blood pressure elevation.', NULL, 'Although theophylline and beta-2 agonists are commonly used together to produce bronchodilation, it may be appropriate to monitor patient response as well as serum potassium level, blood pressure and heart rate during coadministration, especially if the beta-2 agonist is administered systemically or by nebulizer.', NULL, 'Although theophylline and beta-2 agonists are commonly used together to produce bronchodilation, it may be appropriate to monitor patient response as well as serum potassium level, blood pressure and heart rate during coadministration, especially if the beta-2 agonist is administered systemically or by nebulizer.  Close monitoring is particularly important in patients with severe asthma, since the potential increases in blood pressure and heart rate may have more serious consequences in the presence of hypoxemia or hypercapnia due to increased myocardial oxygen consumption.  Patients should be advised to notify their physician if they experience worsening of their respiratory condition or potential signs and symptoms of hypokalemia such as fatigue, weakness, myalgia, muscle cramps, numbness, tingling, abdominal pain, constipation, palpitation, and irregular heartbeat.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/292244/', '[1] Upton RA "Pharmacokinetic interactions between theophylline and oth