INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (247178, 'Betamethasone', 'Vilanterol', 'Minor', 'Although they are often combined in clinical practice, the concomitant use of beta-2 adrenergic agonists and corticosteroids may result in additive hypokalemic effects.  Since beta-2 agonists can sometimes cause QT interval prolongation, the development of hypokalemia may potentiate the risk of ventricular arrhythmias including torsade de pointes.', NULL, 'The combination of beta-2 adrenergic agonists and corticosteroids is not uncommon in clinical practice, and we recommend testing patients for potassium as well as electrocardiograms.', NULL, 'The combination of beta-2 adrenergic agonists and corticosteroids is not uncommon in clinical practice, and we recommend testing patients for potassium as well as electrocardiograms.', 'Synergism', NULL, NULL, 'Minor', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/292271/', '[1] "Product Information. Foradil (formoterol)." Novartis Pharmaceuticals  (2001):[2] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[3] Cerner Multum, Inc. "Australian Product Information." O 0[4] Agencia Espala de Medicamentos y Productos Sanitarios Healthcare "Centro de informaci online de medicamentos de la AEMPS - CIMA.  https://cima.aemps.es/cima/publico/home.html"   (2008):', 1767365210);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (247179, 'Betaxolol', 'Vilanterol', 'Moderate', 'Although cardioselective beta-blockers do not generally inhibit the bronchodilating effect of beta-2 adrenergic agonists, they may worsen pulmonary function in patients with asthma or other obstructive airway diseases.  Cardioselective beta-blockers can occasionally precipitate acute bronchospasm in these patients, despite relative selectivity for beta-1 receptors in cardiac tissues.', NULL, 'Beta-blockers, including those with relative cardioselectivity, should generally be avoided in patients with bronchospastic diseases.', NULL, 'Beta-blockers, including those with relative cardioselectivity, should generally be avoided in patients with bronchospastic diseases.  However, given their demonstrated benefit in such conditions as heart failure, myocardial infarction, cardiac arrhythmias and hypertension, cardioselective beta-blockers may be administered with caution to those who do not respond to or tolerate alternative treatment.  The benefits generally outweigh the risks in patients with mild or moderate reactive airway disease that is well controlled on inhaled corticosteroids and beta-2 agonists, provided they have no prior history suggesting a predisposition to severe exacerbations.  The dosage should start low, preferably in divided doses to avoid the higher plasma levels associated with longer dosing intervals, and titrated slowly according to therapeutic response and pulmonary function.  Patients should be advised to contact their physician if they experience worsening of respiratory symptoms, which would warrant a reevaluation of the appropriateness of beta-blocker therapy.  Cardioselective beta-blockers sh