INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (247221, 'Delavirdine', 'Vilanterol', 'Moderate', 'Coadministration with potent inhibitors of CYP450 3A4 may increase the systemic exposure to vilanterol following oral inhalation, as it is primarily metabolized by the isoenzyme.', NULL, 'Due to the risk of cardiovascular adverse effects such as increases in pulse rate and blood pressure and ECG changes such as flattening of the T wave, prolongation of the QTc interval, and ST segment depression, caution is recommended when medications containing vilanterol are coadministered with potent CYP450 3A4 inhibitors such as itraconazole, ketoconazole, posaconazole, voriconazole, conivaptan, nefazodone, cobicistat, delavirdine, protease inhibitors, and ketolide and certain macrolide antibiotics.', NULL, 'Due to the risk of cardiovascular adverse effects such as increases in pulse rate and blood pressure and ECG changes such as flattening of the T wave, prolongation of the QTc interval, and ST segment depression, caution is recommended when medications containing vilanterol are coadministered with potent CYP450 3A4 inhibitors such as itraconazole, ketoconazole, posaconazole, voriconazole, conivaptan, nefazodone, cobicistat, delavirdine, protease inhibitors, and ketolide and certain macrolide antibiotics.  An increased risk of systemic corticosteroid effects should also be considered when fluticasone-vilanterol is used.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/292318/', '[1] "Product Information. Breo Ellipta (fluticasone-vilanterol)." GlaxoSmithKline  (2013):[2] Kempsford R, Norris V, Siederer S "Vilanterol trifenatate, a novel inhaled long-acting beta2 adrenoceptor agonist, is well tolerated in healthy subjects and demonstrates prolonged bronchodilation  in subjects with asthma and COPD." Pulm Pharmacol Ther 26 (2013):  256-64[3] "Product Information. Anoro Ellipta (umeclidinium-vilanterol)." GlaxoSmithKline  (2022):', 1767365210);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (247222, 'Desipramine', 'Vilanterol', 'Moderate', 'Concomitant use of beta-2 adrenergic agonists with tricyclic antidepressants (TCAs) may increase the risk and/or severity of cardiovascular adverse effects such as hypertension, palpitation, chest pain, and arrhythmia.  In addition, both classes of drugs have been reported to produce electrocardiographic changes including flattening of the T wave and prolongation of the QTc interval, and these effects may be additive during coadministration.', NULL, 'Caution is advised if beta-2 agonists are used with TCAs, or within two weeks of discontinuing the latter.', NULL, 'Caution is advised if beta-2 agonists are used with TCAs, or within two weeks of discontinuing the latter.  Cardiovascular status should be closely monitored.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/292319/', '[1] "Product Information. Proventil (albuterol)." Schering Corporation  (2002):[2] "Product Information. Serevent (salmeterol)." Glaxo Wellcome[3] "Product Information. Maxair (pirbuterol)." 3M Pharmaceuticals  (2001):[4] "Product Information. Alupent (metaproterenol)." Boehringer-Ingelheim  (2001):[5] "Product Information. Tornalate (bitolterol)." Apothecon Inc  (2022):[6] "Product Information. Xopenex (levalbuterol)." Sepracor Inc  (2001):[7] "Product Information. Foradil (formoterol)." Novartis Pharmaceuticals  (2001):[8] "Product Information. Brovana (arformoterol)." Sepracor Inc  (2006):[9] "Product Information. Arcapta Neohaler (indacaterol)." Novartis Pharmaceuticals  (2011):[10] "Product Information. Breo Ellipta (fluticasone-vilanterol)." GlaxoSmithKline  (2013):[11] "Product Information. Striverdi Respimat (olodaterol)." Boehringer Ingelheim  (2014):', 1767365210);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (247223, 'Desvenlafaxine', 'Vilanterol', 'Moderate', 'Additive or synergistic effects on blood pressure and heart rate may occur when serotonin-norepinephrine reuptake inhibitors (SNRIs) are combined with sympathomimetic agents such as nasal decongestants, adrenergic bronchodilators, ophthalmic vasoconstrictors, and systemic vasopressors.', NULL, 'Caution is advised if SNRIs are used with other drugs that can increase blood pressure and/or heart rate.', NULL, 'Caution is advised if SNRIs are used with other drugs that can increase blood pressure and/or heart rate.  Blood pressure and pulse should be measured prior to initiating SNRI therapy and monitored at regular intervals thereafter.  Dose reduction or discontinuation of the SNRI should be considered in patients who experience a sustained increase in blood pressure or pulse rate.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/292320/', '[1] "Product Information. Effexor (venlafaxine)." Wyeth-Ayerst Laboratories  (2001):[2] "Product Information. Cymbalta (duloxetine)." Lilly, Eli and Company  (2004):[3] "Product Information. Pristiq (desvenlafaxine)." Wyeth Laboratories  (2008):[4] "Product Information. Savella (milnacipran)." Forest Pharmaceuticals  (2009):[5] "Product Information. Nucynta (tapentadol)." PriCara Pharmaceuticals  (2009):[6] "Product Information. Fetzima (levomilnacipran)." Forest Pharmaceuticals  (2013):', 1767365210);
