INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (247225, 'Dexamethasone', 'Vilanterol', 'Minor', 'Although they are often combined in clinical practice, the concomitant use of beta-2 adrenergic agonists and corticosteroids may result in additive hypokalemic effects.  Since beta-2 agonists can sometimes cause QT interval prolongation, the development of hypokalemia may potentiate the risk of ventricular arrhythmias including torsade de pointes.', NULL, 'The combination of beta-2 adrenergic agonists and corticosteroids is not uncommon in clinical practice, and we recommend testing patients for potassium as well as electrocardiograms.', NULL, 'The combination of beta-2 adrenergic agonists and corticosteroids is not uncommon in clinical practice, and we recommend testing patients for potassium as well as electrocardiograms.', 'Synergism', NULL, NULL, 'Minor', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/292322/', '[1] "Product Information. Foradil (formoterol)." Novartis Pharmaceuticals  (2001):[2] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[3] Cerner Multum, Inc. "Australian Product Information." O 0[4] Agencia Espala de Medicamentos y Productos Sanitarios Healthcare "Centro de informaci online de medicamentos de la AEMPS - CIMA.  https://cima.aemps.es/cima/publico/home.html"   (2008):', 1767365210);
