INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1472219, 'Sotalol', 'Osimertinib', 'Major', 'Class IA (e.g., disopyramide, quinidine, procainamide) and class III (e.g., amiodarone, dofetilide, sotalol) antiarrhythmic agents can cause dose-related prolongation of the QT interval. Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death.', 'DDInter', 1767317476, 'Coadministration of class IA or class III antiarrhythmic agents with other drugs that can prolong the QT interval should preferably be avoided unless benefits are anticipated to outweigh the risks. Caution and clinical monitoring are recommended if concomitant use is required. Patients should be advised to seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope.', 'Synergism', 'Coadministration of class IA or class III antiarrhythmic agents with other drugs that can prolong the QT interval should preferably be avoided unless benefits are anticipated to outweigh the risks.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1472220, 'Sparfloxacin', 'Osimertinib', 'Major', 'Sparfloxacin may cause dose-related prolongation of the QT interval in some patients. Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death.', 'DDInter', 1767317476, 'Coadministration of sparfloxacin with other drugs that can prolong the QT interval is considered contraindicated.', 'Synergism', 'Coadministration of sparfloxacin with other drugs that can prolong the QT interval is considered contraindicated.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1472221, 'St. John''s Wort', 'Osimertinib', 'Major', 'Coadministration with potent inducers of CYP450 3A4 may decrease the plasma concentrations of osimertinib, which has been shown in vitro to be primarily metabolized by the isoenzyme. Reduced therapeutic efficacy of osimertinib should be anticipated.', 'DDInter', 1767317476, 'Concomitant use of osimertinib with the potent CYP450 3A4 inducers should generally be avoided. However, some authorities suggest that if concomitant use is required, the dose of osimertinib should be increased to 160 mg daily during treatment with the potent 3A4 inducer and resumed at 80 mg daily, 3 weeks after discontinuation of the potent 3A4 inducer.', 'Metabolism', 'Concomitant use of osimertinib with the potent CYP450 3A4 inducers should generally be avoided.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1472222, 'Stiripentol', 'Osimertinib', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of osimertinib, which has been shown in vitro to be primarily metabolized by the isoenzyme.', 'DDInter', 1767317476, 'Caution is advised when osimertinib is prescribed with CYP450 3A4 inhibitors. Patients should be monitored for adverse effects such as diarrhea, QT prolongation, torsade de pointes arrhythmia, and cardiomyopathy (e.g., cardiac failure, pulmonary edema, ejection fraction decreases), and the osimertinib dosage adjusted accordingly or treatment discontinued as necessary.', 'Metabolism', 'Caution is advised when osimertinib is prescribed with CYP450 3A4 inhibitors.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1472223, 'Strontium chloride Sr-89', 'Osimertinib', 'Moderate', 'The concomitant use of bone marrow depressants and strontium-89 chloride may have additive myelosuppressive effects.', 'DDInter', 1767317476, 'Patients should be monitored for excessive bone marrow suppression during treatment with strontium-89 chloride. Dose reductions of the other bone marrow depressants may be necessary. Patients should be advised to contact their physician if they develop signs and symptoms of myelosuppression such as pallor, dizziness, fatigue, lethargy, fainting, easy bruising or bleeding, or signs of infection such as fever, chills, sore throat, body aches, and other influenza-like symptoms.', 'Synergism', 'Patients should be monitored for excessive bone marrow suppression during treatment with strontium-89 chloride.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1472224, 'Sulfasalazine', 'Osimertinib', 'Moderate', 'Coadministration with osimertinib may increase the exposure of breast cancer resistance protein (BCRP) transporter substrates. In vitro studies have shown that osimertinib is a competitive inhibitor of BCRP.', 'DDInter', 1767317476, 'Caution is recommended if osimertinib is used in combination with drugs that are substrates of the BCRP transporter, particularly those with a narrow therapeutic index. Monitoring for signs and symptoms of increased exposure to the BCRP substrate should be considered whenever osimertinib is added to or withdrawn from therapy.', 'Others', 'Caution is recommended if osimertinib is used in combination with drugs that are substrates of the BCRP transporter, particularly those with a narrow therapeutic index.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1472225, 'Sulfinpyrazone', 'Osimertinib', 'Moderate', 'Coadministration with inducers of CYP450 3A4 may decrease the plasma concentrations of osimertinib, which has been shown in vitro to be primarily metabolized by the isoenzyme. Reduced therapeutic efficacy of osimertinib may occur. However, no pharmacokinetic data are available from drug interaction studies.', 'DDInter', 1767317476, 'The potential for diminished pharmacologic effects of osimertinib should be considered during coadministration with CYP450 3A4 inducers. Alternative treatments may be required if an interaction is suspected.', 'Metabolism', 'The potential for diminished pharmacologic effects of osimertinib should be considered during coadministration with CYP450 3A4 inducers.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1472226, 'Sunitinib', 'Osimertinib', 'Major', 'Osimertinib may cause dose-related prolongation of the QT interval. Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death.', 'DDInter', 1767317476, 'Caution is recommended if osimertinib is used in combination with other drugs that can prolong the QT interval. ECG and serum electrolytes, including potassium, magnesium and calcium, should be monitored before starting osimertinib therapy and periodically during treatment.', 'Synergism', 'Caution is recommended if osimertinib is used in combination with other drugs that can prolong the QT interval.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1472227, 'Suvorexant', 'Osimertinib', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of osimertinib, which has been shown in vitro to be primarily metabolized by the isoenzyme.', 'DDInter', 1767317476, 'Caution is advised when osimertinib is prescribed with CYP450 3A4 inhibitors. Patients should be monitored for adverse effects such as diarrhea, QT prolongation, torsade de pointes arrhythmia, and cardiomyopathy (e.g., cardiac failure, pulmonary edema, ejection fraction decreases), and the osimertinib dosage adjusted accordingly or treatment discontinued as necessary.', 'Metabolism', 'Caution is advised when osimertinib is prescribed with CYP450 3A4 inhibitors.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1472228, 'Tacrine', 'Osimertinib', 'Moderate', 'Coadministration with osimertinib may decrease the plasma concentrations of drugs that are primarily metabolized by CYP450 1A2. In vitro, osimertinib has been shown to be an inducer of the isoenzyme.', 'DDInter', 1767317476, 'Caution is advised when osimertinib is prescribed with drugs that undergo metabolism by CYP450 1A2. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever osimertinib is added to or withdrawn from therapy. Sensitive CYP450 1A2 substrates or substrates with narrow therapeutic ranges (e.g., theophylline) should preferably be avoided with osimertinib, if possible.', 'Metabolism', 'Caution is advised when osimertinib is prescribed with drugs that undergo metabolism by CYP450 1A2.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1472229, 'Tacrolimus', 'Osimertinib', 'Moderate', 'Coadministration with osimertinib may alter the plasma concentrations of drugs that are primarily metabolized by CYP450 3A4. In vitro, osimertinib has been shown to be a competitive inhibitor as well as inducer of CYP450 3A4.', 'DDInter', 1767317476, 'Concomitant use of osimertinib and sensitive CYP450 3A4 substrates or substrates with narrow therapeutic ranges should generally be avoided. These drugs include, but are not limited to: alfentanil, cisapride, colchicine, cyclosporine, fentanyl, ivacaftor, lovastatin, simvastatin, oral midazolam, triazolam, pimozide, quinidine, sildenafil, ergot derivatives, macrolide immunosuppressants, and vinca alkaloids.', 'Metabolism', 'Concomitant use of osimertinib and sensitive CYP450 3A4 substrates or substrates with narrow therapeutic ranges should generally be avoided.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1472230, 'Talazoparib', 'Osimertinib', 'Moderate', 'Coadministration with inhibitors of P-glycoprotein (P-gp) and/or breast cancer resistance protein (BCRP) may increase the plasma concentrations of talazoparib, which has been shown in vitro to be a substrate of both efflux membrane transporters.', 'DDInter', 1767317476, 'No initial dosage adjustment is recommended by the manufacturer when talazoparib is coadministered with inhibitors of BCRP and/or P-gp other than amiodarone, carvedilol, clarithromycin, itraconazole, or verapamil. However, patients should be closely monitored for adverse effects such as myelosuppression and myelodysplastic syndrome/acute myeloid leukemia, and dosage adjustments made or treatment withheld as needed in accordance with the product labeling.', 'Distribution', 'No initial dosage adjustment is recommended by the manufacturer when talazoparib is coadministered with inhibitors of BCRP and/or P-gp other than amiodarone, carvedilol, clarithromycin, itraconazole, or verapamil.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1472231, 'Talimogene laherparepvec', 'Osimertinib', 'Major', 'Talimogene laherparepvec is a live, attenuated herpes simplex virus. Administration during immunosuppressant or intense antineoplastic therapy may be associated with a risk of potentially life-threatening disseminated herpetic infection due to enhanced virus replication in the presence of diminished immune competence. Patients may be immunosuppressed if they have recently received or are receiving alkylating agents, antimetabolites, radiation, some antirheumatic agents, high dosages of corticosteroids or adrenocorticotropic agents, or long-term topical or inhaled corticosteroids.', 'DDInter', 1767317476, 'Talimogene laherparepvec should not be used in patients receiving immunosuppressive therapy or cancer chemotherapy. Treatment with talimogene laherparepvec may need to be deferred until after such therapy is discontinued and the patient''s immune system has sufficiently recovered.', 'Synergism', 'Talimogene laherparepvec should not be used in patients receiving immunosuppressive therapy or cancer chemotherapy.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1472232, 'Tasimelteon', 'Osimertinib', 'Moderate', 'Coadministration with inducers of CYP450 1A2 and/or 3A4 may decrease the plasma concentrations of tasimelteon, which is primarily metabolized by these isoenzymes.', 'DDInter', 1767317476, 'The potential for diminished therapeutic effects of tasimelteon should be considered during coadministration with inducers of CYP450 1A2 and/or 3A4. Alternative treatments may be required if an interaction is suspected.', 'Metabolism', 'The potential for diminished therapeutic effects of tasimelteon should be considered during coadministration with inducers of CYP450 1A2 and/or 3A4.', NULL, NULL, 'Moderate', 'pharmacodynamic');
