INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1472276, 'Voriconazole', 'Osimertinib', 'Major', 'Coadministration with potent inhibitors of CYP450 3A4 may increase the plasma concentrations of osimertinib, which has been shown in vitro to be primarily metabolized by the isoenzyme. However, no pharmacokinetic data are available from drug interaction studies. Because osimertinib is associated with concentration-dependent prolongation of the QT interval, increased levels may potentiate the risk of ventricular arrhythmias such as torsade de pointes and sudden death.', 'DDInter', 1767317476, 'Concomitant use of osimertinib with potent CYP450 3A4 inhibitors should generally be avoided. If coadministration is required, close monitoring for adverse effects such as diarrhea, QT prolongation, torsade de pointes arrhythmia, and cardiomyopathy (e.g., cardiac failure, pulmonary edema, ejection fraction decreases) is recommended, and the osimertinib dosage adjusted accordingly or treatment discontinued as necessary.', 'Metabolism', 'Concomitant use of osimertinib with potent CYP450 3A4 inhibitors should generally be avoided.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1472277, 'Voxelotor', 'Osimertinib', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of osimertinib, which has been shown in vitro to be primarily metabolized by the isoenzyme.', 'DDInter', 1767317476, 'Caution is advised when osimertinib is prescribed with CYP450 3A4 inhibitors. Patients should be monitored for adverse effects such as diarrhea, QT prolongation, torsade de pointes arrhythmia, and cardiomyopathy (e.g., cardiac failure, pulmonary edema, ejection fraction decreases), and the osimertinib dosage adjusted accordingly or treatment discontinued as necessary.', 'Metabolism', 'Caution is advised when osimertinib is prescribed with CYP450 3A4 inhibitors.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1472278, 'Yellow Fever Vaccine', 'Osimertinib', 'Major', 'The administration of live, attenuated viral or bacterial vaccines during immunosuppressant or intense antineoplastic therapy may be associated with a risk of disseminated infection due to enhanced replication of vaccine virus or bacteria in the presence of diminished immune competence. Patients may be immunosuppressed if they have recently received or are receiving alkylating agents, antimetabolites, radiation, some antirheumatic agents, high dosages of corticosteroids or adrenocorticotropic agents, or long-term topical or inhaled corticosteroids. These patients may also have increased adverse reactions and decreased or suboptimal immunologic response to vaccines.', 'DDInter', 1767317476, 'In general, live attenuated vaccines should not be used in patients receiving immunosuppressive therapy or cancer chemotherapy. Vaccination should be deferred until after such therapy is discontinued and immune function has been restored, usually 4 to 12 weeks after stopping immunosuppressive therapy. A longer waiting period may be necessary following treatment with agents that have a prolonged elimination half-life (e.g., leflunomide, teriflunomide). Current local immunization guidelines should be consulted for recommendations.', 'Others', 'In general, live attenuated vaccines should not be used in patients receiving immunosuppressive therapy or cancer chemotherapy.', NULL, NULL, 'Major', 'pharmacodynamic');
