INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1472480, 'Quetiapine', 'Oxaliplatin', 'Moderate', 'There is some concern that quetiapine may have additive cardiovascular effects in combination with other drugs that are known to prolong the QT interval of the electrocardiogram.', 'DDInter', 1767317476, 'Coadministration of quetiapine with other drugs that can prolong the QT interval should generally be avoided. Caution and clinical monitoring are recommended if concomitant use is required. Patients should be advised to seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope. In addition, if combination therapy with agents with anticholinergic properties is required, caution is advised, particularly in the elderly and those with underlying organic brain disease.', 'Synergism', 'Coadministration of quetiapine with other drugs that can prolong the QT interval should generally be avoided.', NULL, NULL, 'Moderate', 'pharmacodynamic');
