INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (247560, 'Berotralstat', 'Norgestimate', 'Moderate', 'Coadministration with berotralstat may increase the plasma concentrations and effects of drugs that are substrates of CYP450 3A4 and/or 2D6.  The mechanism is decreased clearance due to inhibition of CYP450 3A4 and 2D6 activity by berotralstat.', NULL, 'Caution is advised when berotralstat is coadministered with drugs that are substrates of CYP450 3A4 and/or 2D6, particularly those with a narrow therapeutic index including but not limited to thioridazine, pimozide, cyclosporine, and fentanyl.', NULL, 'Caution is advised when berotralstat is coadministered with drugs that are substrates of CYP450 3A4 and/or 2D6, particularly those with a narrow therapeutic index including but not limited to thioridazine, pimozide, cyclosporine, and fentanyl.  Clinical and laboratory monitoring are recommended following the initiation of berotralstat, and the individual dosage of the concomitant agents adjusted as needed.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/292688/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] "Product Information. Orladeyo (berotralstat)." BioCryst Pharmaceuticals Inc  (2021):', 1767363028);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (247561, 'Bexarotene', 'Norgestimate', 'Major', 'There are no data concerning the potential effect of bexarotene on hormonal contraceptives.  Presumably, coadministration with bexarotene may decrease the efficacy of contraceptives containing low-dose estrogens and progestins due to its induction of CYP450 3A4, the isoenzyme that is primarily responsible for the metabolic clearance of sex hormones.', NULL, 'Women using low-dose hormonal contraceptives should be advised of the risk of breakthrough bleeding and unintended pregnancy during concomitant therapy with bexarotene.', NULL, 'Women using low-dose hormonal contraceptives should be advised of the risk of breakthrough bleeding and unintended pregnancy during concomitant therapy with bexarotene.  Because use of bexarotene is likely associated with major birth defects, it is particularly important that patients not become pregnant during treatment.  Therefore, hormonal contraceptives should not be used as the sole method of birth control in women of childbearing potential treated with bexarotene.  Product labeling recommends that two reliable forms of contraception, one non-hormonal, be used simultaneously during bexarotene therapy and for at least one month following discontinuation of therapy.  Input from a gynecologist or similar expert on adequate contraception, including emergency contraception, should be sought as needed.  Intrauterine systems are unlikely to be significantly affected because of their local action.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/292690/', '[1] "Product Information. Targretin (bexarotene)." Ligand Pharmaceuticals  (2001):[2] Faculty of Sexual & Reproductive Healthcare "FSRH Clinical Guidance: Drug Interactions with Hormonal Contraception.  file:///C:/Users/df033684/Downloads/ceuguidancedruginteractionshormonal.pdf"   (2016):', 1767363028);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (247562, 'Boceprevir', 'Norgestimate', 'Major', 'Coadministration with the hepatitis C virus (HCV) NS3/4A protease inhibitors, boceprevir and telaprevir, may decrease the plasma concentrations and efficacy of hormonal contraceptives.  The mechanism involves induction of CYP450 3A4, the isoenzyme primarily responsible for the metabolic clearance of sex hormones.', NULL, 'Systemic hormonal contraceptives may be continued, but may not be as effective, during and for up to two weeks following treatment with boceprevir or telaprevir.', NULL, 'Systemic hormonal contraceptives may be continued, but may not be as effective, during and for up to two weeks following treatment with boceprevir or telaprevir.  Because HCV NS3/4A protease inhibitors must be used in combination with peginterferon alfa and ribavirin, and use of ribavirin has been associated with significant birth defects and fetal death, it is particularly important that patients not become pregnant during treatment.  Therefore, systemic hormonal contraceptives should not be relied upon as an effective method of birth control in women of childbearing potential treated with HCV NS3/4A protease inhibitors.  Two alternative, effective methods of contraception, which may include intrauterine devices and barrier methods, should be used during HCV NS3/4A protease inhibitor treatment in combination with peginterferon alfa and ribavirin.  Intrauterine systems are unlikely to be significantly affected because of their local action.  Input from a gynecologist or similar expert on adequate contraception, including emergency contraception, should be sought as needed.  Two weeks after completion of HCV NS3/4A protease inhibitor treatment, hormonal contraceptives are again appropriate as one of the two required effective methods of birth control for patients receiving peginterferon alfa and ribavirin.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/292691/', '[1] "Product Information. Victrelis (boceprevir)." Schering-Plough Corporation  (2011):[2] "Product Information. Incivek (telaprevir)." Vertex Pharmaceuticals  (2011):[3] Faculty of Sexual & Reproductive Healthcare "FSRH Clinical Guidance: Drug Interactions with Hormonal Contraception.  file:///C:/Users/df033684/Downloads/ceuguidancedruginteractionshormonal.pdf"   (2016):', 1767363028);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (247563, 'Bosentan', 'Norgestimate', 'Major', 'Coadministration with bosentan may decrease the plasma concentrations and efficacy of contraceptive hormones.  The mechanism is bosentan induction of CYP450 3A4, the isoenzyme primarily responsible for the metabolic clearance of sex hormones.', NULL, 'Hormonal contraceptives, including oral, injectable, transdermal, and implantable forms, may not be reliable during concomitant therapy with bosentan.', NULL, 'Hormonal contraceptives, including oral, injectable, transdermal, and implantable forms, may not be reliable during concomitant therapy with bosentan.  Because use of bosentan is likely associated with major birth defects, it is particularly important that patients not become pregnant during treatment.  Therefore, hormonal contraceptives should not be used as the sole method of birth control in women of childbearing potential treated with bosentan.  Input from a gynecologist or similar expert on adequate contraception, including emergency contraception, should be sought as needed.  Intrauterine systems are unlikely to be significantly affected because of their local action.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/292692/', '[1] "Product Information. Tracleer (bosentan)." Actelion Pharmaceuticals US Inc  (2001):[2] Faculty of Sexual & Reproductive Healthcare "FSRH Clinical Guidance: Drug Interactions with Hormonal Contraception.  file:///C:/Users/df033684/Downloads/ceuguidancedruginteractionshormonal.pdf"   (2016):', 1767363028);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (247564, 'Brigatinib', 'Norgestimate', 'Major', 'Coadministration with brigatinib may decrease the plasma concentrations of contraceptive hormones.  Brigatinib has been shown in vitro to induce CYP450 3A4 at clinically relevant plasma concentrations.  Since contraceptive hormones are known substrates of the isoenzyme, decreased concentrations and therapeutic failure may potentially occur.', NULL, 'Hormonal contraceptives, including oral, injectable, transdermal, and implantable forms, may not be reliable during concomitant therapy with brigatinib.', NULL, 'Hormonal contraceptives, including oral, injectable, transdermal, and implantable forms, may not be reliable during concomitant therapy with brigatinib.  Because use of brigatinib may cause fetal harm, it is particularly important that patients not become pregnant during treatment.  Alternative or additional methods of birth control (i.e., non-hormonal) are recommended during and for at least 4 months after the final dose of brigatinib.  Input from a gynecologist or similar expert on adequate contraception, including emergency contraception, should be sought as needed.  Intrauterine systems are unlikely to be significantly affected because of their local action.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/292693/', '[1] "Product Information. Alunbrig (brigatinib)." Ariad Pharmaceuticals Inc  (2017):', 1767363028);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (247565, 'Brivaracetam', 'Norgestimate', 'Minor', 'Coadministration with brivaracetam at higher than recommended dosages may decrease the plasma concentrations of contraceptive hormones.  The mechanism may involve weak induction of CYP450 3A4 metabolism by brivaracetam.', NULL, 'Based on existing data, the interaction is not expected to be clinically relevant, and no particular precaution appears necessary when brivaracetam is used with oral contraceptives.', NULL, 'Based on existing data, the interaction is not expected to be clinically relevant, and no particular precaution appears necessary when brivaracetam is used with oral contraceptives.', 'Metabolism', NULL, NULL, 'Minor', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/292694/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] Stockis A, Watanabe S, Fauchoux N "Interaction between brivaracetam (100 mg/day) and a combination oral contraceptive: a randomized, double-blind, placebo-controlled study." Epilepsia 55 (2014):  e27-31[3] Stockis A, Rolan P "Effect of brivaracetam (400 mg/day) on the pharmacokinetics and pharmacodynamics  of a combination oral contraceptive in healthy women." J Clin Pharmacol 53 (2013):  1313-21[4] "Product Information. Briviact (brivaracetam)." UCB Pharma Inc  (2016):', 1767363028);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (247566, 'Butabarbital', 'Norgestimate', 'Moderate', 'Coadministration with a barbiturate may reduce the efficacy of contraceptive hormones.The interaction stems from accelerated clearance of contraceptive hormones as well as decreased plasma concentrations of unbound (active) hormones due to induction of hepatic CYP450 enzymatic activity and enhancement of hormone-binding globulin capacity by phenobarbital.  Since all barbiturates are believed to possess enzyme-inducing capabilities, the interaction should be expected with agents in the class other than phenobarbital.', NULL, 'Women using hormonal contraceptives should be advised of the risk of breakthrough bleeding and unintended pregnancy during concomitant therapy with barbiturates.', NULL, 'Women using hormonal contraceptives should be advised of the risk of breakthrough bleeding and unintended pregnancy during concomitant therapy with barbiturates.  Alternative or additional methods of birth control should be used during and for at least one week after short-term and 4 weeks after long-term (greater than 4 weeks) barbiturate therapy.  No precautions or recommendations are available for women using hormone-releasing intrauterine systems, but a significant interaction with these systems is thought to be unlikely due to their local action.  Injectable progestin-only contraceptives are also thought to be unaffected by barbiturates.  Input from a gynecologist or similar expert on adequate contraception, including emergency contraception, should be sought as needed.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/292695/', '[1] Baciewi