INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (247616, 'Flibanserin', 'Norgestimate', 'Moderate', 'Concomitant use of hormonal contraceptives has been associated with increased adverse effects of flibanserin.  The mechanism of interaction has not been established, but may partially involve inhibition of flibanserin metabolism.  Flibanserin is primarily metabolized by CYP450 3A4 and, to a lesser extent, by CYP450 2C19.  Some oral contraceptives are considered weak inhibitors of these isoenzymes.', NULL, 'Caution is advised when flibanserin is used with hormonal contraceptives.', NULL, 'Caution is advised when flibanserin is used with hormonal contraceptives.  Patients should be monitored for signs and symptoms of hypotension, syncope, and central nervous system depression.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/292746/', '[1] "Product Information. Addyi (flibanserin)." Sprout Pharmaceuticals  (2015):', 1767363028);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (247617, 'Fluconazole', 'Norgestimate', 'Moderate', 'Azole antifungal agents may increase the plasma concentrations of estrogens and progestins.  The mechanism is decreased clearance of the hormones due to inhibition of CYP450 3A4 activity by azole antifungals.', NULL, 'During concomitant therapy with azole antifungal agents, patients should be observed for increased or altered pharmacologic response to estrogens and progestins, and dosage(s) adjusted accordingly as necessary.', NULL, 'During concomitant therapy with azole antifungal agents, patients should be observed for increased or altered pharmacologic response to estrogens and progestins, and dosage(s) adjusted accordingly as necessary.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/292747/', '[1] Lazar JD, Wilner KD "Drug interactions with fluconazole." Rev Infect Dis 12 Suppl 3 (1990):  s327-33[2] Pillans PI, Sparrow MJ "Pregnancy associated with a combined oral contraceptive and itraconazole." N Z Med J 106 (1993):  436[3] Devenport MH, Crook D, Wynn V, Lees LJ "Metabolic effects of low-dose fluconazole in healthy female users and non-users of oral contraceptives." Br J Clin Pharmacol 27 (1989):  851-9[4] Sinofsky FE, Pasquale SA "The effect of fluconazole on circulating ethinyl estradiol levels in women taking oral contraceptives." Am J Obstet Gynecol 178 (1998):  300-4[5] Weisberg E "Interactions between oral contraceptives and antifungals antibacterials - Is contraceptive failure the result?." Clin Pharmacokinet 36 (1999):  309-13[6] "Product Information. VFEND (voriconazole)." Pfizer U.S. Pharmaceuticals  (2002):', 1767363028);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (247618, 'Fluvoxamine', 'Norgestimate', 'Moderate', 'Coadministration with fluvoxamine may increase the plasma concentrations of drugs that are substrates of the CYP450 3A4 isoenzyme.  The mechanism is decreased clearance due to competitive inhibition of CYP450 3A4 activity by fluvoxamine.', NULL, 'Caution is advised if fluvoxamine must be used concomitantly with medications that undergo metabolism by CYP450 3A4, particularly those with a narrow therapeutic range.', NULL, 'Caution is advised if fluvoxamine must be used concomitantly with medications that undergo metabolism by CYP450 3A4, particularly those with a narrow therapeutic range.  Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever fluvoxamine is added to or withdrawn from therapy.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/292748/', '[1] "Product Information. Luvox (fluvoxamine)." Solvay Pharmaceuticals Inc  (2001):[2] Damkier P, Hansen LL, Brosen K "Effect of fluvoxamine on the pharmacokinetics of quinidine." Eur J Clin Pharmacol 55 (1999):  451-6', 1767363028);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (247619, 'Fosamprenavir', 'Norgestimate', 'Major', 'Some hormonal contraceptives may decrease the plasma concentrations of amprenavir from fosamprenavir when it is administered in the absence of low-dose ritonavir as a pharmacokinetic booster.  In contrast, fosamprenavir in combination with ritonavir may reduce the plasma levels of certain contraceptive hormones, and there is also an increased risk of transaminase elevations when these agents are used together.  The mechanism of interaction has not been described.', NULL, 'Given the risk of reduced viral susceptibility and resistance development associated with subtherapeutic antiretroviral drug levels, alternative (i.', NULL, 'Given the risk of reduced viral susceptibility and resistance development associated with subtherapeutic antiretroviral drug levels, alternative (i.e., nonhormonal) methods of birth control should be considered in patients treated with fosamprenavir.', 'Others', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/292749/', '[1] "Product Information. Lexiva (fosamprenavir)." GlaxoSmithKline  (2003):', 1767363028);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (247620, 'Fosaprepitant', 'Norgestimate', 'Moderate', 'Aprepitant and its prodrug, fosaprepitant, may reduce the efficacy of low-dose hormonal contraceptives during and for up to 28 days after treatment.  The proposed mechanism is aprepitant induction of CYP450 3A4, the isoenzyme partially responsible for the metabolic clearance of contraceptive hormones.', NULL, 'Women using low-dose hormonal contraceptives should be advised of the risk of breakthrough bleeding and unintended pregnancy during concomitant therapy with aprepitant or fosaprepitant.', NULL, 'Women using low-dose hormonal contraceptives should be advised of the risk of breakthrough bleeding and unintended pregnancy during concomitant therapy with aprepitant or fosaprepitant.  Alternative or additional methods of birth control is recommended during treatment with aprepitant and for one month following the last dose of aprepitant.  Data are not available for single-dosing of aprepitant 40 mg.  However, since the timing of aprepitant administration relative to ovulation could cause contraceptive failure, the same precaution as for the 125 mg/80 mg regimen should be observed.  Intrauterine systems are unlikely to be significantly affected because of their local action.  Input from a gynecologist or similar expert on adequate contraception, including emergency contraception, should be sought as needed.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/292750/', '[1] "Product Information. Emend (aprepitant)." Merck & Co., Inc  (2003):[2] "Product Information. Emend for Injection (fosaprepitant)." Merck & Co., Inc  (2008):[3] Faculty of Sexual & Reproductive Healthcare "FSRH Clinical Guidance: Drug Interactions with Hormonal Contraception.  file:///C:/Users/df033684/Downloads/ceuguidancedruginteractionshormonal.pdf"   (2016):', 1767363028);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (247621, 'Fosphenytoin', 'Norgestimate', 'Major', 'Coadministration with certain anticonvulsants such as carbamazepine, eslicarbazepine, oxcarbazepine, phenobarbital, phenytoin, and primidone may reduce the efficacy of contraceptive hormones.', NULL, 'Women using hormonal contraceptives should be advised of the risk of breakthrough bleeding and unintended pregnancy during concomitant therapy with enzyme-inducing anticonvulsants.', NULL, 'Women using hormonal contraceptives should be advised of the risk of breakthrough bleeding and unintended pregnancy during concomitant therapy with enzyme-inducing anticonvulsants.  Alternative or additional methods of birth control should be used during and for at least two weeks after short-term and 4 weeks after long-term (greater than 4 weeks) anticonvulsant therapy.  If a combination oral contraceptive pill is used, a regimen containing at least 50 mcg of ethinyl estradiol per day or equivalent should be considered.  Although breakthrough bleeding is not necessarily indicative of low ethinyl estradiol serum levels or increased risk of ovulation, some clinicians suggest that women who experience breakthrough bleeding during enzyme-inducing therapy may be prescribed an increased dose of ethinyl estradiol above 50 mcg daily by combining more than one formulation of contraceptive pill if necessary.  For emergency contraception in patients who have used an hepatic enzyme inducer in the past 4 weeks, a non-hormonal emergency contraceptive (e.g., copper intrauterine device) is considered preferable.  If this is not possible, some authorities recommend that the usual dose of levonorgestrel (1.5 mg) should be doubled to 3 mg and taken as a single dose as soon as possible (within 72 hours of unprotected sexual intercourse).  However, there are no data on efficacy, compliance, or side effects of this regimen.  For women with the etonogestrel subdermal implant, the addition of a barrier method is recommended during concomitant use and for 28 days after discontinuation of hepatic enzyme inducing drugs.  It is recommended to remove the implant and to prescribe a nonhormonal method in women who require long-term treatment with hepatic enzyme inducing drugs.  No precautions or recommendations are available for women using hormone-releasing intrauterine systems, but a significant interaction with these systems is thought to be unlikely due to their local action.  Injectable progestin-only contraceptives are also thought to be unaffected by enzyme-inducing drugs.', 'Antagonism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/292751/', '[1] Crawford P, Chadwick DJ, Martin C, et al. "The interaction of phenytoin and carbamazepine with combined oral contraceptive steroids." Br J Clin Pharmacol 30 (1990):  892-6[2] Odlind V, Olsson SE "Enhanced metabolism of levonorgestrel during phenytoin treatment in a woman with norplant implants." Contraception 33 (1986):  257-61[3] Baciewicz AM "Oral contraceptive drug interactions." Ther Drug Monit 7 (1985):  26-35[4] Back DJ, Bates M, Bowden A, et al. "The interaction of phenobarbital and other anticonvulsants with oral contraceptive steroid therapy." Contraception 22 (1980):  495-503[5] Dossetor J "Drug interactions with oral contraceptives." Br Med J 4 (1975):  467-8[6] Furlan AJ, Rothner AD "Anti-epileptic drugs and failure of oral contraceptives." Lancet 1 (1974):  1113[7] Coulam CB, Annegers JF "Do anticonvulsants reduce the efficacy of oral contraceptives?" Epilepsia 20 (1979):  519-26[8] Szoka PR, Edgren RA "Drug interactions with oral contraceptives: compilation and analysis of an adverse experience