INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (247622, 'Fostamatinib', 'Norgestimate', 'Moderate', 'Coadministration with fostamatinib may increase the plasma concentrations of drugs that are substrates of the CYP450 3A4 isoenzyme and/or P-glycoprotein (P-gp) transporter.  The proposed mechanism is decreased clearance in the intestine and/or liver due to inhibition of CYP450 3A4-mediated metabolism and P-gp-mediated efflux by fostamatinib.', NULL, 'Caution is advised when fostamatinib is used concurrently with drugs that are known substrates of CYP450 3A4 and/or P-gp, particularly those with a narrow therapeutic range.', NULL, 'Caution is advised when fostamatinib is used concurrently with drugs that are known substrates of CYP450 3A4 and/or P-gp, particularly those with a narrow therapeutic range.  Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever fostamatinib is added to or withdrawn from therapy.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/292752/', '[1] "Product Information. Tavalisse (fostamatinib)." Rigel Pharmaceuticals  (2018):', 1767363028);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (247623, 'Frovatriptan', 'Norgestimate', 'Minor', 'The concurrent use of oral contraceptives may increase the plasma concentrations of frovatriptan.The mechanism of interaction has not been established, and it is not known if hormonal contraceptives delivered by other routes (intravaginal, transdermal) may similarly affect the pharmacokinetics of frovatriptan.', NULL, 'The concurrent use of oral contraceptives may increase the plasma concentrations of frovatriptan.', NULL, 'The concurrent use of oral contraceptives may increase the plasma concentrations of frovatriptan.  According to the manufacturer, retrospective analysis of pharmacokinetic data from females across trials indicated that the mean peak plasma concentration (Cmax) and area under the concentration-time curve (AUC) of frovatriptan are 30% higher in oral contraceptive users compared to non-users.  The mechanism of interaction has not been established, and it is not known if hormonal contraceptives delivered by other routes (intravaginal, transdermal) may similarly affect the pharmacokinetics of frovatriptan.  Regardless, this interaction is unlikely to be of clinical significance.', 'Others', NULL, NULL, 'Minor', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/292753/', '[1] "Product Information. Frova (frovatriptan)." Endo Laboratories LLC  (2001):', 1767363028);
