INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (247668, 'Norgestimate', 'Miltefosine', 'Moderate', 'The vomiting and/or diarrhea that commonly occur during miltefosine therapy may affect the absorption of oral contraceptives, potentially compromising their efficacy.', NULL, 'Female patients receiving oral contraceptives should be advised to use additional non-hormonal or alternative method(s) of effective contraception if vomiting or diarrhea occurs during miltefosine therapy.', NULL, 'Female patients receiving oral contraceptives should be advised to use additional non-hormonal or alternative method(s) of effective contraception if vomiting or diarrhea occurs during miltefosine therapy.  This is particularly important because miltefosine may cause fetal harm.  Teratogenicity and fetal death were observed in animals administered miltefosine at dosages lower than the recommended human dosage.  Females of reproductive potential should use effective contraception during miltefosine therapy and for 5 months after completion of therapy.', 'Absorption', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/292802/', '[1] "Product Information. Impavido (miltefosine)." Paladin Therapeutics Inc  (2014):', 1767363028);
