INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (247693, 'Pioglitazone', 'Norgestimate', 'Minor', 'Coadministration with another thiazolidinedione, troglitazone, has been shown to decrease the plasma concentrations of contraceptive hormones.  The proposed mechanism is induction of hormone metabolism via CYP450 3A4.', NULL, 'Coadministration with another thiazolidinedione, troglitazone, has been shown to decrease the plasma concentrations of contraceptive hormones.', NULL, 'Coadministration with another thiazolidinedione, troglitazone, has been shown to decrease the plasma concentrations of contraceptive hormones.  The proposed mechanism is induction of hormone metabolism via CYP450 3A4.  In 15 healthy female subjects, troglitazone (60 mg once daily for 3 weeks) decreased the mean steady-state peak plasma concentration (Cmax) and area under the concentration-time curve (AUC) of ethinyl estradiol and norethindrone (35 mcg-1 mg) by approximately 30% each compared to administration of the contraceptive alone.  Four subjects experienced breakthrough bleeding during the cycle with troglitazone.  There are no data on the use of contraceptive hormones with pioglitazone.  Because pioglitazone may be a weak inducer of CYP450 3A4, product labeling recommends additional caution in women requiring contraception.  However, some studies have reported no significant effect of pioglitazone on CYP450 3A4.  Rosiglitazone labeling states that the drug given in a dosage of 4 mg twice daily had no clinically relevant effect on the pharmacokinetics of oral contraceptives (ethinyl estradiol and norethindrone).  Intrauterine systems are unlikely to be significantly affected because of their local action.', 'Metabolism', NULL, NULL, 'Minor', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/292832/', '[1] "Product Information. Avandia (rosiglitazone)." SmithKline Beecham  (2001):[2] "Product Information. Actos (pioglitazone)." Takeda Pharmaceuticals America  (2001):[3] Loi CM, Stern R, Koup JR, Vassos AB, Knowlton P, Sedman AJ "Effect of troglitazone on the pharmacokinetics of an oral contraceptive agent." J Clin Pharmacol 39 (1999):  410-7[4] Glazer NB, Cheatham WW "Thiazolidinediones for type 2 diabetes - No evidence exists that pioglitazone induces hepatic cytochrome P450 isoform CYP3A4." Br Med J 322 (2001):  235-6[5] Nowak SN, Edwards DJ, Clarke A, Anderson GD, Jaber LA "Pioglitazone: effect on CYP3A4 activity." J Clin Pharmacol 42 (2002):  1299-302', 1767363028);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (247694, 'Pitolisant', 'Norgestimate', 'Moderate', 'Coadministration with pitolisant may decrease the plasma concentrations of contraceptive hormones.  The proposed mechanism is induction of the CYP450 3A4-mediated metabolism of estrogens and progestins by pitolisant and its main metabolites.', NULL, 'The manufacturer recommends that alternative, nonhormonal methods of effective contraception should be used during and for at least 21 days after pitolisant therapy.', NULL, 'The manufacturer recommends that alternative, nonhormonal methods of effective contraception should be used during and for at least 21 days after pitolisant therapy.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/292834/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] Faculty of Sexual & Reproductive Healthcare "FSRH Clinical Guidance: Drug Interactions with Hormonal Contraception.  file:///C:/Users/df033684/Downloads/ceuguidancedruginteractionshormonal.pdf"   (2016):[3] "Product Information. Wakix (pitolisant)." Harmony Biosciences, LLC  (2019):', 1767363028);
