INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (247696, 'Pralsetinib', 'Norgestimate', 'Moderate', 'Coadministration with pralsetinib may render hormonal contraceptives ineffective.  The mechanism for the potential interaction has not been established and there are no clinical data regarding the use of pralsetinib with hormonal contraceptives.', NULL, 'Until further data are available, females of reproductive potential should be advised to use effective non-hormonal contraception during treatment and for 2 weeks after the final dose.', NULL, 'Until further data are available, females of reproductive potential should be advised to use effective non-hormonal contraception during treatment and for 2 weeks after the final dose.  In addition, men receiving pralsetinib should use effective contraception during treatment and for 1 week after the final dose.', 'Others', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/292836/', '[1] "Product Information. Gavreto (pralsetinib)." Blueprint Medicines Corporation  (2020):', 1767363028);
