INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (247715, 'Selegiline', 'Norgestimate', 'Moderate', 'The use of oral contraceptives or hormone replacement therapy may significantly increase selegiline bioavailability.  In one study, the AUC of selegiline increased by 10 to 20-fold in subjects taking oral contraceptives. The proposed mechanism is inhibition of CYP450 3A hepatic metabolism.', NULL, 'Monitoring for signs and symptoms of selegiline toxicity such as excessive MAO inhibition and hypertension when female sex steroids are added and monitoring for reduced selegiline effectiveness when steroids are withdrawn is recommended.', NULL, 'Monitoring for signs and symptoms of selegiline toxicity such as excessive MAO inhibition and hypertension when female sex steroids are added and monitoring for reduced selegiline effectiveness when steroids are withdrawn is recommended.  Selegiline dosage and dietary adjustments (avoiding tyramine) may be required.  Patients should be advised to notify their physician promptly if they experience symptoms such as severe headache, dizziness, nervousness, weakness, irritability, altered consciousness, seizures, confusion, ataxia, abdominal cramping, hyperpyrexia, shivering, pupillary dilation, diaphoresis, or tachycardia.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/292855/', '[1] Laine K, Anttila M, Helminen A, Karnani H, Huupponen R "Dose linearity study of selegiline pharmacokinetics after oral administration: evidence for strong drug interaction with female sex steroids." Br J Clin Pharmacol 47 (1999):  249-54', 1767363028);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (247716, 'Selpercatinib', 'Norgestimate', 'Moderate', 'Coadministration with selpercatinib may increase the plasma concentrations of drugs that are primarily metabolized by the CYP450 3A4 isoenzyme.  The mechanism is reduced clearance due to inhibition of CYP450 3A4 by selpercatinib.  The interaction may be particularly important for sensitive substrates or those that demonstrate a narrow therapeutic index.', NULL, 'Caution is advised when selpercatinib is used concomitantly with drugs that are substrates of CYP450 3A4, particularly those with a narrow therapeutic range.', NULL, 'Caution is advised when selpercatinib is used concomitantly with drugs that are substrates of CYP450 3A4, particularly those with a narrow therapeutic range.  Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever selpercatinib is added to or withdrawn from therapy.  Patients should be monitored for the development of adverse effects.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/292856/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] "Product Information. Retevmo (selpercatinib)." Lilly, Eli and Company  (2020):', 1767363028);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (247717, 'Selumetinib', 'Norgestimate', 'Moderate', 'Coadministration with selumetinib may reduce the effectiveness of hormonal contraceptives.', NULL, 'Until further data are available, females of reproductive potential using hormonal contraceptives should be advised to add a barrier method during treatment with selumetinib and for 1 week after the final dose.', NULL, 'Until further data are available, females of reproductive potential using hormonal contraceptives should be advised to add a barrier method during treatment with selumetinib and for 1 week after the final dose.  In addition, men with female partners of reproductive potential should also use effective contraception during treatment with selumetinib and for 1 week after the last dose.', 'Antagonism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/292857/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] Cerner Multum, Inc. "Australian Product Information." O 0', 1767363028);
