INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (247729, 'Tacrolimus', 'Norgestimate', 'Moderate', 'Coadministration of sirolimus or tacrolimus with other drugs that are also metabolized by CYP450 3A4 may result in elevated plasma concentrations of the macrolide immunosuppressant and/or the coadministered drug(s).  The mechanism is decreased drug clearance due to competitive inhibition of CYP450 3A4 activity.', NULL, 'Pharmacologic responses and/or plasma drug levels should be monitored more closely whenever a macrolide immunosuppressant or another substrate of CYP450 3A4 is added to or withdrawn from therapy, and the dosage(s) adjusted as necessary.', NULL, 'Pharmacologic responses and/or plasma drug levels should be monitored more closely whenever a macrolide immunosuppressant or another substrate of CYP450 3A4 is added to or withdrawn from therapy, and the dosage(s) adjusted as necessary.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/292870/', '[1] "Product Information. Prograf (tacrolimus)." Fujisawa  (2001):[2] "Product Information. Rapamune (sirolimus)." Wyeth-Ayerst Laboratories  (2001):', 1767363028);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (247730, 'Tazemetostat', 'Norgestimate', 'Moderate', 'Coadministration with tazemetostat may decrease the plasma concentrations and efficacy of hormonal contraceptives.  The mechanism involves induction of CYP450 3A4, the isoenzyme primarily responsible for the metabolic clearance of sex hormones.', NULL, 'Although the clinical significance of the interaction is unknown, caution is advised when tazemetostat is prescribed in combination with hormonal contraceptives, including all oral, injectable, transdermal, vaginal, and implantable forms.', NULL, 'Although the clinical significance of the interaction is unknown, caution is advised when tazemetostat is prescribed in combination with hormonal contraceptives, including all oral, injectable, transdermal, vaginal, and implantable forms.  Because use of tazemetostat may cause fetal harm, it is particularly important that female patients not become pregnant during treatment.  Females of reproductive potential should be advised to use alternative or additional methods of birth control during treatment with tazemetostat and for 6 months after the final dose.  Input from a gynecologist or similar expert on adequate contraception, including emergency contraception, should be sought as needed.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/292871/', '[1] "Product Information. Tazverik (tazemetostat)." Epizyme, Inc  (2020):', 1767363028);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (247731, 'Telaprevir', 'Norgestimate', 'Major', 'Coadministration with the hepatitis C virus (HCV) NS3/4A protease inhibitors, boceprevir and telaprevir, may decrease the plasma concentrations and efficacy of hormonal contraceptives.  The mechanism involves induction of CYP450 3A4, the isoenzyme primarily responsible for the metabolic clearance of sex hormones.', NULL, 'Systemic hormonal contraceptives may be continued, but may not be as effective, during and for up to two weeks following treatment with boceprevir or telaprevir.', NULL, 'Systemic hormonal contraceptives may be continued, but may not be as effective, during and for up to two weeks following treatment with boceprevir or telaprevir.  Because HCV NS3/4A protease inhibitors must be used in combination with peginterferon alfa and ribavirin, and use of ribavirin has been associated with significant birth defects and fetal death, it is particularly important that patients not become pregnant during treatment.  Therefore, systemic hormonal contraceptives should not be relied upon as an effective method of birth control in women of childbearing potential treated with HCV NS3/4A protease inhibitors.  Two alternative, effective methods of contraception, which may include intrauterine devices and barrier methods, should be used during HCV NS3/4A protease inhibitor treatment in combination with peginterferon alfa and ribavirin.  Intrauterine systems are unlikely to be significantly affected because of their local action.  Input from a gynecologist or similar expert on adequate contraception, including emergency contraception, should be sought as needed.  Two weeks after completion of HCV NS3/4A protease inhibitor treatment, hormonal contraceptives are again appropriate as one of the two required effective methods of birth control for patients receiving peginterferon alfa and ribavirin.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/292873/', '[1] "Product Information. Victrelis (boceprevir)." Schering-Plough Corporation  (2011):[2] "Product Information. Incivek (telaprevir)." Vertex Pharmaceuticals  (2011):[3] Faculty of Sexual & Reproductive Healthcare "FSRH Clinical Guidance: Drug Interactions with Hormonal Contraception.  file:///C:/Users/df033684/Downloads/ceuguidancedruginteractionshormonal.pdf"   (2016):', 1767363028);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (247732, 'Telithromycin', 'Norgestimate', 'Moderate', 'Coadministration with telithromycin may increase the plasma concentrations of drugs that are substrates of the CYP450 3A4 isoenzyme.  The mechanism is decreased clearance due to inhibition of CYP450 3A4 activity by telithromycin.', NULL, 'Caution is advised if telithromycin must be used concurrently with medications that undergo metabolism by CYP450 3A4, particularly those with a narrow therapeutic range.', NULL, 'Caution is advised if telithromycin must be used concurrently with medications that undergo metabolism by CYP450 3A4, particularly those with a narrow therapeutic range.  Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever telithromycin is added to or withdrawn from therapy.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/292874/', '[1] "Product Information. Ketek (telithromycin)." Aventis Pharmaceuticals  (2004):[2] European Agency for the Evaluation of Medicinal Products. Committee for Proprietary Medicinal Products "European Public Assessment Report Ketek (telithromycin) (Rev. 2)  http:www.emea.eu.int/humandocs/Humans/EPAR/Ketek/Ketek.htm"   (2004):', 1767363028);
