INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (247768, 'Desogestrel', 'Aprepitant', 'Moderate', 'Aprepitant and its prodrug, fosaprepitant, may reduce the efficacy of low-dose hormonal contraceptives during and for up to 28 days after treatment.  The proposed mechanism is aprepitant induction of CYP450 3A4, the isoenzyme partially responsible for the metabolic clearance of contraceptive hormones.', NULL, 'Women using low-dose hormonal contraceptives should be advised of the risk of breakthrough bleeding and unintended pregnancy during concomitant therapy with aprepitant or fosaprepitant.', NULL, 'Women using low-dose hormonal contraceptives should be advised of the risk of breakthrough bleeding and unintended pregnancy during concomitant therapy with aprepitant or fosaprepitant.  Alternative or additional methods of birth control is recommended during treatment with aprepitant and for one month following the last dose of aprepitant.  Data are not available for single-dosing of aprepitant 40 mg.  However, since the timing of aprepitant administration relative to ovulation could cause contraceptive failure, the same precaution as for the 125 mg/80 mg regimen should be observed.  Intrauterine systems are unlikely to be significantly affected because of their local action.  Input from a gynecologist or similar expert on adequate contraception, including emergency contraception, should be sought as needed.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/292917/', '[1] "Product Information. Emend (aprepitant)." Merck & Co., Inc  (2003):[2] "Product Information. Emend for Injection (fosaprepitant)." Merck & Co., Inc  (2008):[3] Faculty of Sexual & Reproductive Healthcare "FSRH Clinical Guidance: Drug Interactions with Hormonal Contraception.  file:///C:/Users/df033684/Downloads/ceuguidancedruginteractionshormonal.pdf"   (2016):', 1767363028);
