INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (247772, 'Desogestrel', 'Atazanavir', 'Moderate', 'Coadministration with atazanavir may increase the plasma concentrations of ethinyl estradiol and norethindrone and possibly other contraceptive hormones.  The mechanism is atazanavir inhibition of CYP450 3A4 and UGT1A1, the enzymes responsible for the metabolic clearance of contraceptive hormones.', NULL, 'The manufacturer recommends alternate methods of nonhormonal contraception during atazanavir or atazanavir-ritonavir therapy.', NULL, 'The manufacturer recommends alternate methods of nonhormonal contraception during atazanavir or atazanavir-ritonavir therapy.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/292922/', '[1] "Product Information. Reyataz (atazanavir)." Bristol-Myers Squibb  (2003):', 1767363028);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (247773, 'Desogestrel', 'Atorvastatin', 'Minor', 'Coadministration with atorvastatin may increase the plasma concentrations of estrogens and progestins.  The exact mechanism of interaction is unknown but may be related to competitive inhibition of CYP450 3A4, the isoenzyme responsible for the metabolism of atorvastatin, estrogens, some (if not all) progestins, and other steroids.', NULL, 'The clinical significance of these alterations is unknown but should be considered when selecting oral contraceptive and hormone replacement doses.', NULL, 'The clinical significance of these alterations is unknown but should be considered when selecting oral contraceptive and hormone replacement doses.', 'Metabolism', NULL, NULL, 'Minor', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/292923/', '[1] "Product Information. Lipitor (atorvastatin)." Parke-Davis  (2001):', 1767363028);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (247774, 'Desogestrel', 'Berotralstat', 'Moderate', 'Coadministration with berotralstat may decrease the efficacy of hormonal contraceptives that require CYP450 2C9 for conversion of prodrug to active metabolite, such as desogestrel.  Berotralstat is a weak inhibitor of CYP450 2C9, the isoenzyme responsible for conversion of desogestrel to its active metabolite, etonogestrel.', NULL, 'Females using only desogestrel for contraception should be advised to use alternative or additional methods of birth control (e.', NULL, 'Females using only desogestrel for contraception should be advised to use alternative or additional methods of birth control (e.g., barrier method, injectable progesterone, combination oral hormonal contraception) during treatment with berotralstat and for at least 1 month after the final dose.  Input from a gynecologist or similar expert on adequate contraception, including emergency contraception, should be sought as needed.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/292926/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] "Product Information. Orladeyo (berotralstat)." BioCryst Pharmaceuticals Inc  (2021):', 1767363028);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (247775, 'Desogestrel', 'Bexarotene', 'Major', 'There are no data concerning the potential effect of bexarotene on hormonal contraceptives.  Presumably, coadministration with bexarotene may decrease the efficacy of contraceptives containing low-dose estrogens and progestins due to its induction of CYP450 3A4, the isoenzyme that is primarily responsible for the metabolic clearance of sex hormones.', NULL, 'Women using low-dose hormonal contraceptives should be advised of the risk of breakthrough bleeding and unintended pregnancy during concomitant therapy with bexarotene.', NULL, 'Women using low-dose hormonal contraceptives should be advised of the risk of breakthrough bleeding and unintended pregnancy during concomitant therapy with bexarotene.  Because use of bexarotene is likely associated with major birth defects, it is particularly important that patients not become pregnant during treatment.  Therefore, hormonal contraceptives should not be used as the sole method of birth control in women of childbearing potential treated with bexarotene.  Product labeling recommends that two reliable forms of contraception, one non-hormonal, be used simultaneously during bexarotene therapy and for at least one month following discontinuation of therapy.  Input from a gynecologist or similar expert on adequate contraception, including emergency contraception, should be sought as needed.  Intrauterine systems are unlikely to be significantly affected because of their local action.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/292928/', '[1] "Product Information. Targretin (bexarotene)." Ligand Pharmaceuticals  (2001):[2] Faculty of Sexual & Reproductive Healthcare "FSRH Clinical Guidance: Drug Interactions with Hormonal Contraception.  file:///C:/Users/df033684/Downloads/ceuguidancedruginteractionshormonal.pdf"   (2016):', 1767363028);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (247776, 'Desogestrel', 'Boceprevir', 'Major', 'Coadministration with the hepatitis C virus (HCV) NS3/4A protease inhibitors, boceprevir and telaprevir, may decrease the plasma concentrations and efficacy of hormonal contraceptives.  The mechanism involves induction of CYP450 3A4, the isoenzyme primarily responsible for the metabolic clearance of sex hormones.', NULL, 'Systemic hormonal contraceptives may be continued, but may not be as effective, during and for up to two weeks following treatment with boceprevir or telaprevir.', NULL, 'Systemic hormonal contraceptives may be continued, but may not be as effective, during and for up to two weeks following treatment with boceprevir or telaprevir.  Because HCV NS3/4A protease inhibitors must be used in combination with peginterferon alfa and ribavirin, and use of ribavirin has been associated with significant birth defects and fetal death, it is particularly important that patients not become pregnant during treatment.  Therefore, systemic hormonal contraceptives should not be relied upon as an effective method of birth control in women of childbearing potential treated with HCV NS3/4A protease inhibitors.  Two alternative, effective methods of contraception, which may include intrauterine devices and barrier methods, should be used during HCV NS3/4A protease inhibitor treatment in combination with peginterferon alfa and ribavirin.  Intrauterine systems are unlikely to be significantly affected because of their local action.  Input from a gynecologist or similar expert on adequate contraception, including emergency contraception, should be sought as needed.  Two weeks after completion of HCV NS3/4A protease inhibitor treatment, hormonal contraceptives are again appropriate as one of the two required effective methods of birth control for patients receiving peginterferon alfa and ribavirin.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/292929/', '[1] "Product Information. Victrelis (boceprevir)." Schering-Plough Corporation  (2011):[2] "Product Information. Incivek (telaprevir)." Vertex Pharmaceuticals  (2011):[3] Faculty of Sexual & Reproductive Healthcare "FSRH Clinical Guidance: Drug Interactions with Hormonal Contraception.  file:///C:/Users/df033684/Downloads/ceuguidancedruginteractionshormonal.pdf"   (2016):', 1767363028);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (247777, 'Desogestrel', 'Bosentan', 'Major', 'Coadministration with bosentan may decrease the plasma concentrations and efficacy of contraceptive hormones.  The mechanism is bosentan induction of CYP450 3A4, the isoenzyme primarily responsible for the metabolic clearance of sex hormones.', NULL, 'Hormonal contraceptives, including oral, injectable, transdermal, and implantable forms, may not be reliable during concomitant therapy with bosentan.', NULL, 'Hormonal contraceptives, including oral, injectable, transdermal, and implantable forms, may not be reliable during concomitant therapy with bosentan.  Because use of bosentan is likely associated with major birth defects, it is particularly important that patients not become pregnant during treatment.  Therefore, hormonal contraceptives should not be used as the sole method of birth control in women of childbearing potential treated with bosentan.  Input from a gynecologist or similar expert on adequate contraception, including emergency contraception, should be sought as needed.  Intrauterine systems are unlikely to be significantly affected because of their local action.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/292930/', '[1] "Product Information. Tracleer (bosentan)." Actelion Pharmaceuticals US Inc  (2001):[2] Faculty of Sexual & Reproductive Healthcare "FSRH Clinical Guidance: Drug Interactions with Hormonal Contraception.  file:///C:/Users/df033684/Downloads/ceuguidancedruginteractionshormonal.pdf"   (2016):', 1767363028);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (247778, 'Desogestrel', 'Brigatinib', 'Major', 'Coadministration with brigatinib may decrease the plasma concentrations of contraceptive hormones.  Brigatinib has been shown in vitro to induce CYP450 3A4 at clinically relevant plasma concentrations.  Since contraceptive hormones are known substrates of the isoenzyme, decreased concentrations and therapeutic failure may potentially occur.', NULL, 'Hormonal contraceptives, including oral, injectable, transdermal, and implantable forms, may not be reliable during concomitant therapy with brigatinib.', NULL, 'Hormonal contraceptives, including oral, injectable, transdermal, and implantable forms, may not be reliable during concomitant therapy with brigatinib.  Because use of brigatinib may cause fetal harm, it is particularly important that patients not become pregnant during treatment.  Alternative or additional methods of birth control (i.e., non-hormonal) are recommended during and for at least 4 months after the final dose of brigatinib.  Input from a gynecologist or similar expert on adequate contraception, including emergency contraception, should be sought as needed.  Intrauterine systems are unlikely to be significantly affected because of their local action.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/292931/', '[1] "Product Information. Alunbrig (brigatinib)." Ariad Pharmaceuticals Inc  (2017):', 1767363028);
