INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (247800, 'Desogestrel', 'Cyclosporine', 'Moderate', 'Coadministration with some estrogens or progestins may increase the plasma concentrations of cyclosporine.  The risk of cyclosporine-induced nephrotoxicity, hepatotoxicity, and neurotoxicity may be increased.  The proposed mechanism is competitive inhibition of cyclosporine metabolism via CYP450 3A4, of which estrogens and progestins are also substrates.', NULL, 'Caution is advised during concomitant therapy with cyclosporine.', NULL, 'Caution is advised during concomitant therapy with cyclosporine.  Cyclosporine blood levels as well as renal and hepatic function should be checked frequently and the dosage adjusted accordingly, particularly following initiation or discontinuation of hormonal contraceptives in patients who are stabilized on their cyclosporine regimen.  Patients should be advised to notify their physician if they experience signs and symptoms of hepatotoxicity (e.g., fever, rash, anorexia, nausea, vomiting, fatigue, right upper quadrant pain, dark urine, jaundice) or neurotoxicity (e.g., tremor, convulsions).', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/292953/', '[1] Maurer G "Metabolism of cyclosporine." Transplant Proc 17 (1985):  19-26[2] Deray G, Le Hoang P, Cacoub P, et al. "Oral contraceptive interaction with cyclosporin." Lancet 1 (1987):  158-9', 1767363028);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (247801, 'Desogestrel', 'Dabrafenib', 'Major', 'Coadministration with dabrafenib may decrease the plasma concentrations and efficacy of contraceptive hormones.  The mechanism is dabrafenib induction of CYP450 3A4, the isoenzyme primarily responsible for the metabolic clearance of sex hormones.', NULL, 'Hormonal contraceptives, including oral, injectable, transdermal, and implantable forms, may not be reliable during concomitant therapy with dabrafenib.', NULL, 'Hormonal contraceptives, including oral, injectable, transdermal, and implantable forms, may not be reliable during concomitant therapy with dabrafenib.  Because use of dabrafenib may cause fetal harm, it is particularly important that patients not become pregnant during treatment.  Female patients of reproductive potential should use a highly effective, non-hormonal method of contraception during treatment and for 4 weeks after treatment.  Input from a gynecologist or similar expert on adequate contraception, including emergency contraception, should be sought as needed.  Intrauterine systems are unlikely to be significantly affected because of their local action.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/292954/', '[1] "Product Information. Tafinlar (dabrafenib)." GlaxoSmithKline  (2013):[2] Faculty of Sexual & Reproductive Healthcare "FSRH Clinical Guidance: Drug Interactions with Hormonal Contraception.  file:///C:/Users/df033684/Downloads/ceuguidancedruginteractionshormonal.pdf"   (2016):', 1767363028);
