INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (247828, 'Flibanserin', 'Desogestrel', 'Moderate', 'Concomitant use of hormonal contraceptives has been associated with increased adverse effects of flibanserin.  The mechanism of interaction has not been established, but may partially involve inhibition of flibanserin metabolism.  Flibanserin is primarily metabolized by CYP450 3A4 and, to a lesser extent, by CYP450 2C19.  Some oral contraceptives are considered weak inhibitors of these isoenzymes.', NULL, 'Caution is advised when flibanserin is used with hormonal contraceptives.', NULL, 'Caution is advised when flibanserin is used with hormonal contraceptives.  Patients should be monitored for signs and symptoms of hypotension, syncope, and central nervous system depression.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/292982/', '[1] "Product Information. Addyi (flibanserin)." Sprout Pharmaceuticals  (2015):', 1767363028);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (247829, 'Fluconazole', 'Desogestrel', 'Moderate', 'Azole antifungal agents may increase the plasma concentrations of estrogens and progestins.  The mechanism is decreased clearance of the hormones due to inhibition of CYP450 3A4 activity by azole antifungals.', NULL, 'During concomitant therapy with azole antifungal agents, patients should be observed for increased or altered pharmacologic response to estrogens and progestins, and dosage(s) adjusted accordingly as necessary.', NULL, 'During concomitant therapy with azole antifungal agents, patients should be observed for increased or altered pharmacologic response to estrogens and progestins, and dosage(s) adjusted accordingly as necessary.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/292983/', '[1] Lazar JD, Wilner KD "Drug interactions with fluconazole." Rev Infect Dis 12 Suppl 3 (1990):  s327-33[2] Pillans PI, Sparrow MJ "Pregnancy associated with a combined oral contraceptive and itraconazole." N Z Med J 106 (1993):  436[3] Devenport MH, Crook D, Wynn V, Lees LJ "Metabolic effects of low-dose fluconazole in healthy female users and non-users of oral contraceptives." Br J Clin Pharmacol 27 (1989):  851-9[4] Sinofsky FE, Pasquale SA "The effect of fluconazole on circulating ethinyl estradiol levels in women taking oral contraceptives." Am J Obstet Gynecol 178 (1998):  300-4[5] Weisberg E "Interactions between oral contraceptives and antifungals antibacterials - Is contraceptive failure the result?." Clin Pharmacokinet 36 (1999):  309-13[6] "Product Information. VFEND (voriconazole)." Pfizer U.S. Pharmaceuticals  (2002):', 1767363028);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (247830, 'Fluvoxamine', 'Desogestrel', 'Moderate', 'Coadministration with fluvoxamine may increase the plasma concentrations of drugs that are substrates of the CYP450 3A4 isoenzyme.  The mechanism is decreased clearance due to competitive inhibition of CYP450 3A4 activity by fluvoxamine.', NULL, 'Caution is advised if fluvoxamine must be used concomitantly with medications that undergo metabolism by CYP450 3A4, particularly those with a narrow therapeutic range.', NULL, 'Caution is advised if fluvoxamine must be used concomitantly with medications that undergo metabolism by CYP450 3A4, particularly those with a narrow therapeutic range.  Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever fluvoxamine is added to or withdrawn from therapy.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/292984/', '[1] "Product Information. Luvox (fluvoxamine)." Solvay Pharmaceuticals Inc  (2001):[2] Damkier P, Hansen LL, Brosen K "Effect of fluvoxamine on the pharmacokinetics of quinidine." Eur J Clin Pharmacol 55 (1999):  451-6', 1767363028);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (247831, 'Fosamprenavir', 'Desogestrel', 'Major', 'Some hormonal contraceptives may decrease the plasma concentrations of amprenavir from fosamprenavir when it is administered in the absence of low-dose ritonavir as a pharmacokinetic booster.  In contrast, fosamprenavir in combination with ritonavir may reduce the plasma levels of certain contraceptive hormones, and there is also an increased risk of transaminase elevations when these agents are used together.  The mechanism of interaction has not been described.', NULL, 'Given the risk of reduced viral susceptibility and resistance development associated with subtherapeutic antiretroviral drug levels, alternative (i.', NULL, 'Given the risk of reduced viral susceptibility and resistance development associated with subtherapeutic antiretroviral drug levels, alternative (i.e., nonhormonal) methods of birth control should be considered in patients treated with fosamprenavir.', 'Others', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/292985/', '[1] "Product Information. Lexiva (fosamprenavir)." GlaxoSmithKline  (2003):', 1767363028);
