INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (247839, 'Glycerol phenylbutyrate', 'Desogestrel', 'Moderate', 'Coadministration with glycerol phenylbutyrate may decrease the systemic exposure to and therapeutic efficacy of drugs that are substrates of CYP450 3A4.  Glycerol phenylbutyrate, its active moiety phenylbutyrate, and active metabolite phenylacetic acid are considered weak CYP450 3A4 inducers in vivo.', NULL, 'Concomitant use of glycerol phenylbutyrate with sensitive CYP450 3A4 substrates (e.', NULL, 'Concomitant use of glycerol phenylbutyrate with sensitive CYP450 3A4 substrates (e.g., oral midazolam) or CYP450 3A4 substrates with a narrow therapeutic index (e.g., alfentanil, oral contraceptives, quinidine, and cyclosporine) should be done with caution and monitoring for reduced efficacy.  Dosage adjustment as well as clinical and laboratory monitoring may be appropriate whenever glycerol phenylbutyrate is added to or withdrawn from therapy.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/292993/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] "Product Information. Ravicti (glycerol phenylbutyrate)." Hyperion Therapeutics Inc  (2013):', 1767363028);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (247840, 'Golimumab', 'Desogestrel', 'Moderate', 'Plasma concentrations of drugs that are CYP450 substrates may decrease following the initiation of interleukin (IL) inhibitors, tumor necrosis factor (TNF) blockers, or interferon (IFN) inhibitors in patients with chronic inflammatory diseases.  Because the formation of hepatic CYP450 enzymes is down-regulated during infection and chronic inflammation by increased levels of certain cytokines (e.g., interleukins-1, -6, and -10; tumor necrosis factor alpha; interferons), treatment targeting these cytokines may restore or normalize CYP450 enzyme levels resulting in increased metabolism of these drugs.', NULL, 'Caution is advised when treatments targeting cytokines such as interleukins, tumor necrosis factors, or interferons are prescribed to patients receiving concomitant drugs that are CYP450 substrates, particularly those with narrow therapeutic ranges (e.', NULL, 'Caution is advised when treatments targeting cytokines such as interleukins, tumor necrosis factors, or interferons are prescribed to patients receiving concomitant drugs that are CYP450 substrates, particularly those with narrow therapeutic ranges (e.g., antiarrhythmics, anticonvulsants, immunosuppressants, theophylline) or sensitive substrates where decreases in plasma levels may be significant or undesirable (e.g., oral contraceptives, statins, benzodiazepines, opioids).  Clinical and/or laboratory monitoring should be considered following the initiation or withdrawal of such treatments, and the dosage(s) of the CYP450 substrate(s) adjusted accordingly.  Clinicians should note that the effects of IL inhibitors, TNF blockers, and IFN inhibitors on CYP450 activities may persist for several weeks after stopping therapy.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/292994/', '[1] "Product Information. Remicade (infliximab)." Centocor Inc  (2001):[2] "Product Information. Amevive (alefacept)." Biogen  (2003):[3] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[4] "Product Information. Arcalyst (rilonacept)." Regeneron Pharmaceuticals Inc  (2008):[5] "Product Information. Stelara (ustekinumab)." Centocor Inc  (2009):[6] "Product Information. Simponi (golimumab)." Centocor Inc  (2009):[7] "Product Information. Ilaris (canakinumab)." Novartis Pharmaceuticals  (2009):[8] "Product Information. Actemra (tocilizumab)." Genentech  (2010):[9] "Product Information. Sylvant (siltuximab)." Janssen Biotech, Inc.  (2014):[10] "Product Information. Cosentyx (secukinumab)." Novartis Pharmaceuticals  (2015):[11] "Product Information. Taltz Autoinjector (ixekizumab)." Eli Lilly and Company  (2016):[12] "Product Information. Kevzara (sarilumab)." sanofi-aventis  (2017):[13] "Product Information. Ilumya (tildrakizumab)." Merck & Co., Inc  (2018):[14] "Product Information. Gamifant (emapalumab)." Sobi Inc  (2018):[15] "Product Information. Skyrizi (risankizumab)." AbbVie US LLC  (2019):', 1767363028);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (247841, 'Griseofulvin', 'Desogestrel', 'Major', 'Limited clinical data suggest that griseofulvin may reduce the efficacy of contraceptive hormones.The proposed mechanism is accelerated clearance of the hormones due to induction of hepatic CYP450 enzymes by griseofulvin.', NULL, 'Women using low-dose hormonal contraceptives should be advised of the risk of breakthrough bleeding and unintended pregnancy during concomitant therapy with griseofulvin.', NULL, 'Women using low-dose hormonal contraceptives should be advised of the risk of breakthrough bleeding and unintended pregnancy during concomitant therapy with griseofulvin.  Because griseofulvin has been shown to be teratogenic in animal studies and is capable of inducing aneuploidy (abnormal segregation of chromosomes following cell division) in mammalian cells in vitro and in vivo, it is particularly important that patients not become pregnant during treatment.  Therefore, additional methods of birth control should be used during and for one month after griseofulvin therapy.  Input from a gynecologist or similar expert on adequate contraception, including emergency contraception, should be sought as needed.  Intrauterine systems are unlikely to be significantly affected because of their local action.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/292995/', '[1] Weber JC "Interaction between oral contraceptives and griseofulvin." Br Med J 288 (1984):  1125-6[2] McDaniel PA, Caldroney RD "Oral contraceptives and griseofulvin interaction." Drug Intell Clin Pharm 20 (1986):  384[3] Cote J "Interaction of griseofulvin and oral contraceptive