INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (247864, 'Desogestrel', 'Lapatinib', 'Moderate', 'Coadministration with lapatinib may increase the plasma concentrations of drugs that are substrates of the CYP450 2C8 isoenzyme, CYP450 3A4 isoenzyme, and/or P-glycoprotein (P-gp) efflux transporter.  The mechanism is decreased clearance via these routes due to inhibition by lapatinib.', NULL, 'Caution is advised if lapatinib must be used concurrently with medications that are substrates of the CYP450 2C8 isoenzyme, CYP450 3A4 isoenzyme, and/or P-gp efflux transporter, particularly those with a narrow therapeutic range.', NULL, 'Caution is advised if lapatinib must be used concurrently with medications that are substrates of the CYP450 2C8 isoenzyme, CYP450 3A4 isoenzyme, and/or P-gp efflux transporter, particularly those with a narrow therapeutic range.  Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever lapatinib is added to or withdrawn from therapy.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/293018/', '[1] "Product Information. Tykerb (lapatinib)." Novartis Pharmaceuticals  (2007):', 1767363028);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (247865, 'Desogestrel', 'Larotrectinib', 'Moderate', 'Coadministration with larotrectinib may increase the plasma concentrations of drugs that are substrates of the CYP450 3A4 isoenzyme.', NULL, 'Caution is advised when larotrectinib is used concomitantly with drugs that are substrates of CYP450 3A4, particularly those with a narrow therapeutic range.', NULL, 'Caution is advised when larotrectinib is used concomitantly with drugs that are substrates of CYP450 3A4, particularly those with a narrow therapeutic range.  Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever larotrectinib is added to or withdrawn from therapy.  Patients should be monitored for the development of adverse effects.  Some authorities recommend avoiding concomitant use of larotrectinib and sensitive CYP450 3A4 substrates or substrates with narrow therapeutic ranges.  These drugs include, but are not limited to:  alfentanil, cisapride, colchicine, cyclosporine, fentanyl, ivacaftor, lovastatin, simvastatin, oral midazolam, triazolam, pimozide, quinidine, sildenafil, ergot derivatives, macrolide immunosuppressants, and vinca alkaloids.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/293019/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] Cerner Multum, Inc. "Australian Product Information." O 0[3] "Product Information. Vitrakvi (larotrectinib)." Bayer Pharmaceutical Inc  (2018):', 1767363028);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (247866, 'Desogestrel', 'Lefamulin', 'Moderate', 'When administered orally, lefamulin may increase the plasma concentrations of drugs that are primarily metabolized by the CYP450 3A4 isoenzyme.  Based on interaction with midazolam, a sensitive CYP450 3A4 substrate, lefamulin may be a moderate CYP450 3A4 inhibitor.', NULL, 'Caution is advised when oral lefamulin is used concomitantly with drugs that are substrates of CYP450 3A4, particularly sensitive substrates or those with a narrow therapeutic range.', NULL, 'Caution is advised when oral lefamulin is used concomitantly with drugs that are substrates of CYP450 3A4, particularly sensitive substrates or those with a narrow therapeutic range.  Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever oral lefamulin is added to or withdrawn from therapy.  Patients should be monitored for the development of adverse effects.  The prescribing information for concomitant medications should be consulted to assess the benefits versus risks of coadministration of a moderate CYP450 3A4 inhibitor like lefamulin and for any dosage adjustments that may be required.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/293020/', '[1] "Product Information. Xenleta (lefamulin)." Nabriva Therapeutics US, Inc.  (2019):', 1767363028);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (247867, 'Desogestrel', 'Lesinurad', 'Moderate', 'Coadministration with lesinurad may decrease the plasma concentrations of contraceptive hormones and reduce their effectiveness.  The interaction has not been evaluated in pharmacokinetic studies.  However, lesinurad is a weak inducer of CYP450 3A4, the isoenzyme responsible for the metabolic clearance of contraceptive hormones.  Lesinurad has been shown to induce the metabolism of other CYP450 3A4 substrates in interaction studies.', NULL, 'Hormonal contraceptives, including oral, injectable, transdermal, and implantable forms, may not be reliable when used with lesinurad.', NULL, 'Hormonal contraceptives, including oral, injectable, transdermal, and implantable forms, may not be reliable when used with lesinurad.  Female patients of reproductive potential receiving hormonal contraceptives should practice additional methods of contraception during treatment with lesinurad.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/293022/', '[1] "Product Information. Zurampic (lesinurad)." Astra-Zeneca Pharmaceuticals  (2015):', 1767363028);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (247868, 'Desogestrel', 'Letermovir', 'Moderate', 'Coadministration with letermovir may increase the plasma concentrations of drugs that are substrates of CYP450 3A4.  In vitro and in vivo studies have shown that both estrogens and progestins are partially metabolized by CYP450 3A4.', NULL, 'Monitor estrogen/progestin therapy for altered pharmacologic response and adjust dosage(s) accordingly whenever letermovir is added to or withdrawn from therapy.', NULL, 'Monitor estrogen/progestin therapy for altered pharmacologic response and adjust dosage(s) accordingly whenever letermovir is added to or withdrawn from therapy.  When choosing an oral contraceptive, consider choosing an ethinyl estradiol-levonorgestrel product as this combination has been studied with letermovir and no clinically significant interaction has been found.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/293023/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] Cerner Multum, Inc. "Australian Product Information." O 0[3] "Product Information. Prevymis (letermovir)." Merck & Co., Inc  (2017):', 1767363028);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (247869, 'Desogestrel', 'Linaclotide', 'Moderate', 'The absorption and efficacy of oral contraceptives may be reduced if severe diarrhea occurs with linaclotide treatment.  Linaclotide has been associated with severe and prolonged diarrhea.', NULL, 'In patients who develop severe diarrhea during treatment with linaclotide, European labeling recommends use of an additional contraceptive method to prevent possible failure of oral contraception.', NULL, 'In patients who develop severe diarrhea during treatment with linaclotide, European labeling recommends use of an additional contraceptive method to prevent possible failure of oral contraception.', 'Absorption', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/293025/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] EMEA. European Medicines Agency "EPARs. European Union Public Assessment Reports.  http://www.ema.europa.eu/ema/index.jsp?curl=pages/includes/medicines/medicines_landingpage.jsp&mid"   (2007):[3] "Product Information. Linzess (linaclotide)." Forest Pharmaceuticals  (2012):', 1767363028);
