INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (247903, 'Desogestrel', 'Pexidartinib', 'Major', 'Coadministration with pexidartinib may decrease the plasma concentration and efficacy of contraceptive hormones.  The proposed mechanism is increased clearance due to pexidartinib-mediated induction of CYP450 3A4, the isoenzyme partially responsible for the metabolic clearance of sex hormones and other steroids.', NULL, 'Women using hormonal contraceptives should be advised of the risk of breakthrough bleeding and unintended pregnancy during concomitant therapy with pexidartinib.', NULL, 'Women using hormonal contraceptives should be advised of the risk of breakthrough bleeding and unintended pregnancy during concomitant therapy with pexidartinib.  Alternative or additional methods of birth control should be used during and for at least one month after the final dose of pexidartinib.  In addition, male patients with female partners of reproductive potential should use effective contraception during and for at least one week after the final dose of pexidartinib.  No precautions or recommendations are available for women using hormone-releasing intrauterine systems, but a significant interaction with these systems is thought to be unlikely due to their local action.  Input from a gynecologist or similar expert on adequate contraception, including emergency contraception, should be sought as needed.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/293066/', '[1] "Product Information. Turalio (pexidartinib)." Daiichi Sankyo, Inc.  (2019):', 1767363028);
