INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (247929, 'Desogestrel', 'Secukinumab', 'Moderate', 'Plasma concentrations of drugs that are CYP450 substrates may decrease following the initiation of interleukin (IL) inhibitors, tumor necrosis factor (TNF) blockers, or interferon (IFN) inhibitors in patients with chronic inflammatory diseases.  Because the formation of hepatic CYP450 enzymes is down-regulated during infection and chronic inflammation by increased levels of certain cytokines (e.g., interleukins-1, -6, and -10; tumor necrosis factor alpha; interferons), treatment targeting these cytokines may restore or normalize CYP450 enzyme levels resulting in increased metabolism of these drugs.', NULL, 'Caution is advised when treatments targeting cytokines such as interleukins, tumor necrosis factors, or interferons are prescribed to patients receiving concomitant drugs that are CYP450 substrates, particularly those with narrow therapeutic ranges (e.', NULL, 'Caution is advised when treatments targeting cytokines such as interleukins, tumor necrosis factors, or interferons are prescribed to patients receiving concomitant drugs that are CYP450 substrates, particularly those with narrow therapeutic ranges (e.g., antiarrhythmics, anticonvulsants, immunosuppressants, theophylline) or sensitive substrates where decreases in plasma levels may be significant or undesirable (e.g., oral contraceptives, statins, benzodiazepines, opioids).  Clinical and/or laboratory monitoring should be considered following the initiation or withdrawal of such treatments, and the dosage(s) of the CYP450 substrate(s) adjusted accordingly.  Clinicians should note that the effects of IL inhibitors, TNF blockers, and IFN inhibitors on CYP450 activities may persist for several weeks after stopping therapy.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/293093/', '[1] "Product Information. Remicade (infliximab)." Centocor Inc  (2001):[2] "Product Information. Amevive (alefacept)." Biogen  (2003):[3] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[4] "Product Information. Arcalyst (rilonacept)." Regeneron Pharmaceuticals Inc  (2008):[5] "Product Information. Stelara (ustekinumab)." Centocor Inc  (2009):[6] "Product Information. Simponi (golimumab)." Centocor Inc  (2009):[7] "Product Information. Ilaris (canakinumab)." Novartis Pharmaceuticals  (2009):[8] "Product Information. Actemra (tocilizumab)." Genentech  (2010):[9] "Product Information. Sylvant (siltuximab)." Janssen Biotech, Inc.  (2014):[10] "Product Information. Cosentyx (secukinumab)." Novartis Pharmaceuticals  (2015):[11] "Product Information. Taltz Autoinjector (ixekizumab)." Eli Lilly and Company  (2016):[12] "Product Information. Kevzara (sarilumab)." sanofi-aventis  (2017):[13] "Product Information. Ilumya (tildrakizumab)." Merck & Co., Inc  (2018):[14] "Product Information. Gamifant (emapalumab)." Sobi Inc  (2018):[15] "Product Information. Skyrizi (risankizumab)." AbbVie US LLC  (2019):', 1767363028);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (247930, 'Desogestrel', 'Selegiline', 'Moderate', 'The use of oral contraceptives or hormone replacement therapy may significantly increase selegiline bioavailability.  In one study, the AUC of selegiline increased by 10 to 20-fold in subjects taking oral contraceptives. The proposed mechanism is inhibition of CYP450 3A hepatic metabolism.', NULL, 'Monitoring for signs and symptoms of selegiline toxicity such as excessive MAO inhibition and hypertension when female sex steroids are added and monitoring for reduced selegiline effectiveness when steroids are withdrawn is recommended.', NULL, 'Monitoring for signs and symptoms of selegiline toxicity such as excessive MAO inhibition and hypertension when female sex steroids are added and monitoring for reduced selegiline effectiveness when steroids are withdrawn is recommended.  Selegiline dosage and dietary adjustments (avoiding tyramine) may be required.  Patients should be advised to notify their physician promptly if they experience symptoms such as severe headache, dizziness, nervousness, weakness, irritability, altered consciousness, seizures, confusion, ataxia, abdominal cramping, hyperpyrexia, shivering, pupillary dilation, diaphoresis, or tachycardia.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/293094/', '[1] Laine K, Anttila M, Helminen A, Karnani H, Huupponen R "Dose linearity study of selegiline pharmacokinetics after oral administration: evidence for strong drug interaction with female sex steroids." Br J Clin Pharmacol 47 (1999):  249-54', 1767363028);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (247931, 'Desogestrel', 'Selpercatinib', 'Moderate', 'Coadministration with selpercatinib may increase the plasma concentrations of drugs that are primarily metabolized by the CYP450 3A4 isoenzyme.  The mechanism is reduced clearance due to inhibition of CYP450 3A4 by selpercatinib.  The interaction may be particularly important for sensitive substrates or those that demonstrate a narrow therapeutic index.', NULL, 'Caution is advised when selpercatinib is used concomitantly with drugs that are substrates of CYP450 3A4, particularly those with a narrow therapeutic range.', NULL, 'Caution is advised when selpercatinib is used concomitantly with drugs that are substrates of CYP450 3A4, particularly those with a narrow therapeutic range.  Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever selpercatinib is added to or withdrawn from therapy.  Patients should be monitored for the development of adverse effects.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/293095/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] "Product Information. Retevmo (selpercatinib)." Lilly, Eli and Company  (2020):', 1767363028);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (247932, 'Desogestrel', 'Selumetinib', 'Moderate', 'Coadministration with selumetinib may reduce the effectiveness of hormonal contraceptives.', NULL, 'Until further data are available, females of reproductive potential using hormonal contraceptives should be advised to add a barrier method during treatment with selumetinib and for 1 week after the final dose.', NULL, 'Until further data are available, females of reproductive potential using hormonal contraceptives should be advised to add a barrier method during treatment with selumetinib and for 1 week after the final dose.  In addition, men with female partners of reproductive potential should also use effective contraception during treatment with selumetinib and for 1 week after the last dose.', 'Antagonism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/293096/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] Cerner Multum, Inc. "Australian Product Information." O 0', 1767363028);
