INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (247962, 'Desogestrel', 'Voxelotor', 'Moderate', 'Coadministration with voxelotor may increase the plasma concentrations of drugs that are primarily metabolized by the CYP450 3A4 isoenzyme.  The mechanism is reduced clearance due to inhibition of CYP450 3A4 by voxelotor.  The interaction may be significant for sensitive CYP450 3A4 substrates or those that demonstrate a narrow therapeutic index.', NULL, 'Caution is advised when voxelotor is used concomitantly with drugs that undergo metabolism by CYP450 3A4.', NULL, 'Caution is advised when voxelotor is used concomitantly with drugs that undergo metabolism by CYP450 3A4.  Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever voxelotor is added to or withdrawn from therapy.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/293130/', '[1] "Product Information. Oxbryta (voxelotor)." Global Blood Therapeutics, Inc.  (2019):', 1767363028);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (247963, 'Desogestrel', 'Zolmitriptan', 'Minor', 'Retrospective pharmacokinetic analysis indicate that plasma concentrations of zolmitriptan may be higher in females taking concurrent oral contraceptives.The mechanism and clinical significance are unknown.', NULL, 'Retrospective pharmacokinetic analysis indicate that plasma concentrations of zolmitriptan may be higher in females taking concurrent oral contraceptives.', NULL, 'Retrospective pharmacokinetic analysis indicate that plasma concentrations of zolmitriptan may be higher in females taking concurrent oral contraceptives.  Mean Cmax and AUC of zolmitriptan were 30% and 50% higher, respectively, and Tmax was delayed by one-half hour in females taking oral contraceptives.  The mechanism and clinical significance are unknown.  The effect of zolmitriptan on the pharmacokinetics of oral contraceptives has not been reported.  Patients should be advised to notify their physician promptly if they experience sedation, dizziness, paresthesia, chest pain or tightness, shortness of breath, or irregular heartbeats.', 'Others', NULL, NULL, 'Minor', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/293131/', '[1] "Product Information. Zomig (zolmitriptan)." Astra-Zeneca Pharmaceuticals  (2001):', 1767363028);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (247964, 'Abametapir (topical)', 'Artemether', 'Moderate', 'Based on in vitro inhibition data, a single topical application of abametapir lotion may increase plasma concentrations of drugs that are substrates of CYP450 3A4, CYP450 2B6, and CYP450 1A2 isoenzymes and increase their systemic concentrations.', NULL, 'Use of CYP450 3A4, CYP450 2B6, and CYP450 1A2 substrates should generally be avoided within 2 weeks after topical application of abametapir lotion.', NULL, 'Use of CYP450 3A4, CYP450 2B6, and CYP450 1A2 substrates should generally be avoided within 2 weeks after topical application of abametapir lotion.  If this is not feasible, the manufacturer recommends avoiding use of abametapir lotion.  If abametapir is used, monitor for increased toxicity of the CYP450 3A4, CYP450 2B6, and/or CYP450 1A2 substrate.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/293132/', '[1] "Product Information. Xeglyze (abametapir topical)." Dr. Reddy''s Laboratories Inc  (2020):', 1767363028);
