INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (247966, 'Abarelix', 'Lumefantrine', 'Major', 'Artemether-lumefantrine may cause prolongation of the QT interval.  Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death.', NULL, 'Coadministration of artemether-lumefantrine with other drugs that can prolong the QT interval should generally be avoided.', NULL, 'Coadministration of artemether-lumefantrine with other drugs that can prolong the QT interval should generally be avoided.  Caution is recommended when these drugs are used following treatment with artemether-lumefantrine due to the long elimination half-life of lumefantrine (3 to 6 days).  Patients should be advised to seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope.', 'Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/293134/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] EMEA. European Medicines Agency "EPARs. European Union Public Assessment Reports.  http://www.ema.europa.eu/ema/index.jsp?curl=pages/includes/medicines/medicines_landingpage.jsp&mid"   (2007):[3] Cerner Multum, Inc. "Australian Product Information." O 0[4] "Product Information. Coartem (artemether-lumefantrine)." Novartis Pharmaceuticals  (2009):', 1767363028);
