INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (248471, 'Efavirenz', 'Somapacitan', 'Moderate', 'Coadministration with inducers of CYP450 3A4 may decrease the plasma concentrations of efavirenz, which is primarily metabolized by the isoenzyme.', NULL, 'Given the risk of reduced viral susceptibility and resistance development associated with subtherapeutic antiretroviral drug levels, caution is advised if efavirenz is prescribed in combination with CYP450 3A4 inducers.', NULL, 'Given the risk of reduced viral susceptibility and resistance development associated with subtherapeutic antiretroviral drug levels, caution is advised if efavirenz is prescribed in combination with CYP450 3A4 inducers.  Close clinical and laboratory monitoring of antiretroviral response is recommended.  Alternative treatment that do not affect efavirenz metabolism should be considered whenever possible.', 'Metabolism', 'Lamivudine, Tenofovir alafenamide, Simeprevir, Cabotegravir, Zanamivir, Valaciclovir, Oseltamivir, Acyclovir, Brincidofovir, Stavudine, Remdesivir, More', 'Corticotropin, Mecasermin, Sermorelin, Tesamorelin, Thyrotropin alfa, Tetracosactide, Mecasermin rinfabate, Pegvisomant', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/293719/', '[1] "Product Information. Sustiva (efavirenz)." DuPont Pharmaceuticals  (2001):[2] Marzolini C, Telenti A, Decosterd LA, Greub G, Biollaz J, Buclin T "Efavirenz plasma levels can predict treatment failure and central nervous system side effects in HIV-1-infected patients." Aids 15 (2001):  71-5', 1767369485);
