    ) VALUES (1472560, 'Valaciclovir', 'Oxaliplatin', 'Moderate', 'The concurrent use of valaciclovir with other nephrotoxic drugs may increase the risk of renal toxicity. Patients who are elderly, debilitated, dehydrated, or who have preexisting renal dysfunction may have a higher risk of developing adverse reactions. Cases of acute renal failure have been reported.', NULL, 'Close clinical and laboratory monitoring of renal function and drug serum concentrations (if clinically appropriate) is recommended.', NULL, 'Close clinical and laboratory monitoring of renal function and drug serum concentrations (if clinically appropriate) is recommended.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317476);
INSERT OR IGNORE INTO drug_interactions (
