INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (248677, 'Eptifibatide', 'Phenyl salicylate', 'Moderate', 'Although aspirin is routinely given with glycoprotein IIb/IIIa inhibitors, be cognizant of the increased risk of bleeding associated with this combination due to the additive inhibitory effects of salicylates on platelet function.  Arterial puncture sites may be most problematic.', NULL, 'It is recommended that these drugs be used together only when indicated by skilled personnel who are qualified and equipped to detect and treat bleeding complications.', NULL, 'It is recommended that these drugs be used together only when indicated by skilled personnel who are qualified and equipped to detect and treat bleeding complications.  Patients should be closely monitored for signs of bleeding, especially at arterial puncture sites.  Therapy should be discontinued immediately if serious or uncontrollable bleeding or thrombocytopenia occurs.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/293938/', '[1] "Product Information. ReoPro (abciximab)." Lilly, Eli and Company  (2001):[2] "Product Information. Integrilin (eptifibatide)." Schering Corporation  (2001):[3] Klinkhardt U, Kirchmaier CM, Westrup D, Graff J, Mahnel R, Breddin HK, Harder S "Ex vivo-in vitro interaction between aspirin, clopidogrel, and the glycoprotein IIb/IIIa inhibitors abciximab and SR121566A." Clin Pharmacol Ther 67 (2000):  305-13[4] Hirsch J, Dalen J, Guyatt G, American College of Chest Physicians "The sixth (2000) ACCP guidelines for antithrombotic therapy for prevention and treatment of thrombosis. American College of Physicians." Chest 119(1 Suppl) (2001):  1S-2