INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (248688, 'Fondaparinux', 'Phenyl salicylate', 'Moderate', 'Theoretically, salicylates may potentiate the effects of anticoagulants and increase the risk of bleeding.', NULL, 'Until further data are available, products containing salicylates, especially if given chronically or in high dosages, should preferably be avoided in patients receiving anticoagulants.', NULL, 'Until further data are available, products containing salicylates, especially if given chronically or in high dosages, should preferably be avoided in patients receiving anticoagulants.  Close clinical and laboratory observation for bleeding complications is recommended if concurrent therapy is necessary.  The same precaution should be observed with the use of salicylate-related agents such as salicylamide because of their structural and pharmacological similarities.   Ambulatory patients should be advised to promptly report any signs of bleeding to their physician, including pain, swelling, headache, dizziness, weakness, prolonged bleeding from cuts, increased menstrual flow, vaginal bleeding, nosebleeds, bleeding of gums from brushing, unusual bleeding or bruising, red or brown urine, or red or black stools.  Patients should also be counseled to avoid any other over-the-counter salicylate products.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/293950/', '[1] Koch-Weser J, Sellers EM "Drug interactions with coumarin anticoagulants (first of two parts)." N Engl J Med 285 (1971):  487-98[2] Koch-Weser J, Sellers EM "Drug interactions with coumarin anticoagulants (second of two parts)." N Engl J Med 285 (1971):  547-58[3] Barrow MV, Quick DT, Cunningham RW "Salicylate hypoprothrombinemia in rheumatoid arthritis with liver disease. Report of two cases." Arch Intern Med 120 (1967):  620-4[4] Weiss HJ, Aledort LM, Kochwa S "The effect of salicylates on the hemostatic properties of platelets in man." J Clin Invest 47 (1968):  2169-80[5] Fausa O "Salicylate-induced hypoprothrombinemia: a report of four cases." Acta Med Scand 188 (1970):  403-8[6] Wells PS, Holbrook AM, Crowther NR, Hirsh J "Interactions of warfarin with drugs and food." Ann Intern Med 121 (1994):  676-83[7] American Medical Association, Division of Drugs and Toxicology "Drug evaluations annual 1994." Chicago, IL: American Medical Association;  (1994):[8] Penning-van Beest F, Erkens J, Petersen KU, Koelz HR, Herings R "Main comedications associated with major bleeding during anticoagulant therapy with coumarins." Eur J Clin Pharmacol 61 (2005):  439-44', 1767363028);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (248689, 'Fosinopril', 'Phenyl salicylate', 'Moderate', 'Nonsteroidal anti-inflammatory drugs (NSAIDs) may attenuate the antihypertensive effects of ACE inhibitors.  The proposed mechanism is NSAID-induced inhibition of renal prostaglandin synthesis, which results in unopposed pressor activity producing hypertension.  In addition, NSAIDs can cause fluid retention, which also affects blood pressure.  Some NSAIDs may also alter the pharmacokinetics of certain ACE inhibitors.', NULL, 'Patients receiving ACE inhibitors who require prolonged (greater than 1 week) concomitant therapy with an NSAID should have blood pressure monitored more closely following initiation, discontinuation, or change of dosage of the NSAID.', NULL, 'Patients receiving ACE inhibitors who require prolonged (greater than 1 week) concomitant therapy with an NSAID should have blood pressure monitored more closely following initiation, discontinuation, or change of dosage of the NSAID.  Renal function should also be evaluated periodically during prolonged coadministration.  The interaction is not expected to occur with low doses (e.g., low-dose aspirin) or intermittent short-term administration of NSAIDs.', 'Antagonism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/293951/', '[1] Moore TJ, Crantz FR, Hollenberg NK "Contribution of prostaglandins to the antihypertensive action of captopril in essential hypertension." Hypertension 3 (1981):  168-73[2] Radack KL, Deck CC, Bloomfield SS "Ibuprofen interferes with the efficacy of antihypertensive drugs: a randomized, double-blind, placebo-controlled trial of ibuprofen compared with aceta