INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (248722, 'Phenyl salicylate', 'Leflunomide', 'Major', 'The recent, concomitant, or subsequent use (without the recommended leflunomide washout period or procedure) of other agents known to induce hepatotoxicity including NSAIDs and salicylates may potentiate the risk of liver injury associated with leflunomide.  The risk is thought to extend to teriflunomide, its principal active metabolite, because recommended dosages of both result in a similar range of plasma concentrations of teriflunomide.', NULL, 'Patients receiving leflunomide or teriflunomide in combination with NSAIDs should be closely monitored for hepatotoxicity.', NULL, 'Patients receiving leflunomide or teriflunomide in combination with NSAIDs should be closely monitored for hepatotoxicity.  Liver enzymes and bilirubin should be measured prior to initiation of leflunomide/teriflunomide therapy and at least monthly for the first six months of treatment and every 6 to 8 weeks thereafter.  Patients with preexisting liver disease or elevated baseline liver enzymes (i.e., ALT greater than two times the upper limit of normal) should not receive leflunomide or teriflunomide.  Patients who develop elevated serum ALT greater than three times ULN while receiving these medications should discontinue treatment and be given washout procedures with cholestyramine or activated charcoal to accelerate elimination of leflunomide''s active metabolite from plasma, which otherwise may take up to two years.  Follow-up monitoring should be conducted at least weekly until the ALT value is within normal range, and washout procedures repeated as necessary.  All patients treated with leflunomide or teriflunomide should be advised to seek medical attention if they experience potential signs and symptoms of hepatotoxicity such as fever, rash, itching, anorexia, nausea, vomiting, fatigue, right upper quadrant pain, dark urine, light colored stools, and jaundice.', 'Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/293987/', '[1] "Product Information. Arava (leflunomide)." Hoechst Marion Roussel  (2001):[2] EMEA "EMEA public statement on leflunomide (ARAVA) - severe and serious hepatic reactions.  Available from URL: http://www.eudra.org/emea.html"   (2001):[3] "Product Information. Aubagio (teriflunomide)." Genzyme Corporation  (2012):', 1767363028);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (248723, 'Phenyl salicylate', 'Lepirudin', 'Moderate', 'Theoretically, salicylates may potentiate the effects of anticoagulants and increase the risk of bleeding.', NULL, 'Until further data are available, products containing salicylates, especially if given chronically or in high dosages, should preferably be avoided in patients receiving anticoagulants.', NULL, 'Until further data are available, products containing salicylates, especially if given chronically or in high dosages, should preferably be avoided in patients receiving anticoagulants.  Close clinical and laboratory observation for bleeding complications is recommended if concurrent therapy is necessary.  The same precaution should be observed with the use of salicylate-related agents such as salicylamide because of their structural and pharmacological similarities.   Ambulatory patients should be advised to promptly report any signs of bleeding to their physician, including pain, swelling, headache, dizziness, weakness, prolonged bleeding from cuts, increased menstrual flow, vaginal bleeding, nosebleeds, bleeding of gums from brushing, unusual bleeding or bruising, red or brown urine, or red or black stools.  Patients should also be counseled to avoid any other over-the-counter salicylate products.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/293988/', '[1] Koch-Weser J, Sellers EM "Drug interactions with coumarin anticoagulants (first of two parts)." N Engl J Med 285 (1971):  487-98[2] Koch-Weser J, Sellers EM "Drug interactions with coumarin anticoagulants (second of two parts)." N Engl J Med 285 (1971):  547-58[3] Barrow MV, Quick DT, Cunningham RW "Salicylate hypoprothrombinemia in rheumatoid arthritis with liver disease. Report of two cases." Arch Intern Med 120 (1967):  620-4[4] Weiss HJ, Aledort LM, Kochwa S "The effect of salicylates on the hemostatic properties of platelets in man." J Clin Invest 47 (1968):  2169-80[5] Fausa O "Salicylate-induced hypoprothrombinemia: a report of four cases." Acta Med Scand 188 (1970):  403-8[6] Wells PS, Holbrook AM, Crowther NR, Hirsh J "Interactions of warfarin with drugs and food." Ann Intern Med 121 (1994):  676-83[7] American Medical Association, Division of Drugs and Toxicology "Drug evaluations annual 1994." Chicago, IL: American Medical Association;  (1994):[8] Penning-van Beest F, Erkens J, Petersen KU, Koelz HR, Herings R "Main comedications associated with major bleeding during anticoagulant therapy with coumarins." Eur J Clin Pharmacol 61 (2005):  439-44', 1767363028);
