INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (248739, 'Phenyl salicylate', 'Monomethyl fumarate', 'Moderate', 'The use of fumaric acid esters in combination with medications that can cause nephrotoxicity (e.g., aminoglycosides, diuretics, nonsteroidal anti-inflammatory drugs, cyclosporine, lithium, methotrexate) may increase the potential for adverse renal reactions.', NULL, 'The use of fumaric acid esters in patients who receive concomitant treatment with potentially nephrotoxic agents, particularly for longer durations, has not been evaluated and should be approached with caution.', NULL, 'The use of fumaric acid esters in patients who receive concomitant treatment with potentially nephrotoxic agents, particularly for longer durations, has not been evaluated and should be approached with caution.  Assessment of renal function (e.g., serum creatinine, blood urea nitrogen, urinalysis) is recommended prior to initiating treatment with fumaric acid esters and as clinically indicated during treatment.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/294005/', '[1] "Product Information. Vumerity (diroximel fumarate)." Biogen Australia Pty Ltd  (2022):[2] "Product Information. Tecfidera (dimethyl fumarate)." Biogen Idec Inc  (2023):[3] "Product Information. Tecfidera (dimethyl fumarate)." Biogen Idec Ltd  (2022):[4] "Product Information. Skilarence (dimethyl fumarate)." Almirall Ltd  (2022):[5] "Product Information. Vumerity (diroximel fumarate)." Biogen Idec Ltd  (2022):[6] "Product Information. Vumerity (diroximel fumarate)." Biogen Idec Inc  (2023):[7] "Product Information. Furatec (dimethyl fumarate)." Pharmacor Pty Ltd  (2022):', 1