INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (248759, 'Polyethylene glycol (3350 with electrolytes)', 'Phenyl salicylate', 'Moderate', 'Coadministration with agents that affect renal function or perfusion such as diuretics, ACE inhibitors, angiotensin receptor blockers, and nonsteroidal anti-inflammatory drugs (NSAIDs) may increase the risk and/or severity of fluid and electrolyte disturbances associated with the use of bowel cleansing preparations.', NULL, 'Caution is advised when bowel cleansing preparations are used in patients treated with agents that affect renal function or perfusion, particularly if they are frail or elderly or have preexisting renal impairment.', NULL, 'Caution is advised when bowel cleansing preparations are used in patients treated with agents that affect renal function or perfusion, particularly if they are frail or elderly or have preexisting renal impairment.  Baseline and postprocedure labs including serum electrolytes, BUN, and creatinine should be considered.  Patients should be advised to drink sufficient quantities of clear fluids before, during, and after bowel cleansing.  Hospitalization and intravenous fluid hydration may be appropriate for frail or elderly patients who may be unable to drink an adequate volume of fluid.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/294026/', '[1] "Product Information. Suprep Bowel Prep Kit (magnesium/potassium/sodium sulfates)." Braintree Laboratories  (2010):[2] "Product Information. Suclear (polyethylene glycol 3350 with electrolytes)." Braintree Laboratories  (2013):', 1767363028);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (248760, 'Potassium citrate', 'Phenyl salicylate', 'Moderate', 'Agents that cause urinary alkalinization can reduce serum salicylate concentrations in patients receiving anti-inflammatory dosages of aspirin or other salicylates.  The mechanism involves reduction in salicylate renal tubular reabsorption due to increased urinary pH, resulting in increased renal salicylate clearance especially above urine pH of 7.  This interaction is sometimes exploited in the treatment of salicylate toxicity.', NULL, 'Patients treated chronically with urinary alkalinizers and large doses of salicylates (i.', NULL, 'Patients treated chronically with urinary alkalinizers and large doses of salicylates (i.e. 3 g/day or more) should be monitored for potentially diminished or inadequate analgesic and anti-inflammatory effects, and the salicylate dosage adjusted if necessary.', 'Excretion', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/294027/', '[1] Berg KJ "Acute acetylsalicylic acid poisoning: treatment with forced alkaline diuresis and diuretics." Eur J Clin Pharmacol 12 (1977):  111-6[2] Prescott LF, Balali-Mood M, Critchley JA, Johnstone AF, Proudfoot AT "Diuresis or urinary alkalinisation for salicylate poisoning?" Br Med J (Clin Res Ed) 285 (1982):  1383-6[3] Balali-Mood M, Prescott LF "Failure of alkaline diuresis to enhance diflunisal elimination." Br J Clin Pharmacol 10 (1980):  163-5[4] Berg KJ "Acute effects of acetylsalicylic acid in patients with chronic renal insufficiency." Eur J Clin Pharmacol 11 (1977):  111-6[5] Brouwers JRBJ, Desmet PAGM "Pharmacokinetic-pharmacodynamic drug interactions with nonsteroidal anti-inflammatory drugs." Clin Pharmacokinet 27 (1994):  462-85', 1767363028);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (248761, 'Prasugrel', 'Phenyl salicylate', 'Moderate', 'Concomitant use of prasugrel with other agents that affect hemostasis including aspirin may increase the risk of bleeding.', NULL, 'Although prasugrel may be used with aspirin, caution is advised during coadministration with any salicylate, especially in patients at risk for gastrointestinal ulceration or bleeding.', NULL, 'Although prasugrel may be used with aspirin, caution is advised during coadministration with any salicylate, especially in patients at risk for gastrointestinal ulceration or bleeding.  Close clinical and laboratory observation for hemorrhagic complications is recommended.  Patients should be advised to contact their physician if they develop potential signs and symptoms of gastrointestinal injury such as abdominal pain, bloating, dizziness, lightheadedness, hematemesis, anorexia, and/or black, tarry stools.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/294028/', '[1] "Product Information. Effient (prasugrel)." Lilly, Eli and Company  (2009):', 1767363028);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (248762, 'Prednisolone', 'Phenyl salicylate', 'Moderate', 'Coadministration with corticosteroids may decrease the serum concentrations and therapeutic effects of salicylates.  Likewise, serum salicylate levels may increase following withdrawal of corticosteroid therapy, potentially resulting in salicylate toxicity.  This interaction has been reported in patients receiving intra-articular as well as oral corticosteroids.  One or more mechanisms may be involved, including an increase in the renal clearance and/or an induction of hepatic metabolism of salicylates caused by corticosteroids.  Pharmacologically, the potential for increased gastrointestinal (GI) toxicity, including inflammation, bleeding, ulceration and perforation, should be considered due to additive ulcerogenic effects of these agents (especially aspirin) on the GI mucosa.', NULL, 'Patients treated concomitantly with a corticosteroid may require higher dosages of salicylates or salicylate-like drugs.', NULL, 'Patients treated concomitantly with a corticosteroid may require higher dosages of salicylates or salicylate-like drugs.  Pharmacologic response to these agents should be monitored more closely whenever a corticosteroid is added to or withdrawn from therapy in patients stabilized on their existing salicylate regimen, and the salicylate dosage adjusted as necessary.  During concomitant therapy, patients should be advised to take the medications with food and to immediately report signs and symptoms of GI ulceration and bleeding such as severe abdominal pain, dizziness, lightheadedness, and the appearance of black, tarry stools.  The selective use of prophylactic anti-ulcer therapy (e.g., antacids, H2-antagonists) may be appropriate, particularly in patients with a prior history of peptic ulcer disease or GI bleeding and in elderly and debilitated patients.', 'Metabolism, Excretion', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/294029/', '[1] Baer PA, Shore A, Ikeman RL "Transient fall in serum salicylate levels following intraarticular injection of steroid in patients with rheumatoid arthritis." Arthritis Rheum 30 (1987):  345-7[2] Koren G, Roifman C, Gelfand E, Lavi S, Suria D, Stein L "Corticosteroids-salicylate interaction in a case of juvenile rheumatoid arthritis." Ther Drug Monit 9 (1987):  177-9[3] Edelman J, Potter JM, Hackett LP "The effect of intra-articular steroids on plasma salicylate concentrations." Br J Clin Pharmacol 21 (1986):  301-7[4] Piper JM, Ray WA, Daugherty JR, Griffin MR "Corticosteroid use and peptic ulcer disease: role of nonsteroidal ani-inflammatory drugs." Ann Intern Med 114 (1991):  735-40[5] Hansen RA, Tu W, Wang J, Ambuehl R, McDonald CJ, Murray MD "Risk of adverse gastrointestinal events from inhaled corticosteroids." Pharmacotherapy 28 (2008):  1325-34', 1767363028);
